// FeedbackTribunal.jsx
import React, { useState } from 'react';
import { Star, StarFill } from 'lucide-react';
import axios from 'axios';
import './FeedbackTribunal.css';

const FeedbackTribunal = ({ id_tribunal, id_usuario }) => {
  const [formData, setFormData] = useState({
    numero_protocolo: '',
    comentario: '',
    av_eficiencia: 0,
    av_qualidade: 0,
    av_infraestrutura: 0,
    av_tecnologia: 0,
    av_gestao: 0,
    av_transparencia: 0,
    av_sustentabilidade: 0,
    horario_chegada: '',
    horario_saida: ''
  });

  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const categorias = [
    { key: 'av_eficiencia', label: 'Eficiência Operacional', desc: 'Avalie o tempo de tramitação e produtividade' },
    { key: 'av_qualidade', label: 'Qualidade do Serviço', desc: 'Avalie a qualidade do atendimento prestado' },
    { key: 'av_infraestrutura', label: 'Infraestrutura', desc: 'Avalie as instalações físicas' },
    { key: 'av_tecnologia', label: 'Tecnologia', desc: 'Avalie os sistemas eletrônicos disponíveis' },
    { key: 'av_gestao', label: 'Gestão', desc: 'Avalie a organização e gestão do tribunal' },
    { key: 'av_transparencia', label: 'Transparência', desc: 'Avalie o acesso à informação' },
    { key: 'av_sustentabilidade', label: 'Sustentabilidade', desc: 'Avalie as práticas sustentáveis' }
  ];

  const handleRatingChange = (categoria, valor) => {
    setFormData(prev => ({
      ...prev,
      [categoria]: valor
    }));
  };

  const renderStars = (categoria) => {
    const rating = formData[categoria];
    return (
      <div className="stars-container">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            type="button"
            className="star-button"
            onClick={() => handleRatingChange(categoria, star)}
          >
            {star <= rating ? (
              <StarFill className="star filled" />
            ) : (
              <Star className="star" />
            )}
          </button>
        ))}
      </div>
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');

    try {
      const response = await axios.post('http://localhost:3001/av_tribunais', {
        ...formData,
        id_tribunal,
        id_usuario
      });

      setSuccess(true);
      setTimeout(() => {
        setSuccess(false);
        setFormData({
          numero_protocolo: '',
          comentario: '',
          av_eficiencia: 0,
          av_qualidade: 0,
          av_infraestrutura: 0,
          av_tecnologia: 0,
          av_gestao: 0,
          av_transparencia: 0,
          av_sustentabilidade: 0,
          horario_chegada: '',
          horario_saida: ''
        });
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.error || 'Erro ao enviar avaliação');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="feedback-container">
      <h2 className="feedback-title">Avalie sua experiência</h2>
      
      <form onSubmit={handleSubmit} className="feedback-form">
        <div className="input-group">
          <label htmlFor="numero_protocolo">Número do Protocolo</label>
          <input
            type="text"
            id="numero_protocolo"
            value={formData.numero_protocolo}
            onChange={(e) => setFormData(prev => ({ ...prev, numero_protocolo: e.target.value }))}
            required
            minLength={5}
            maxLength={20}
            placeholder="Digite o número do protocolo"
          />
        </div>

        {categorias.map(({ key, label, desc }) => (
          <div key={key} className="rating-group">
            <div className="rating-header">
              <h3>{label}</h3>
              <p className="rating-description">{desc}</p>
            </div>
            {renderStars(key)}
          </div>
        ))}

        <div className="input-group">
          <label htmlFor="horario_chegada">Horário de Chegada</label>
          <input
            type="time"
            id="horario_chegada"
            value={formData.horario_chegada}
            onChange={(e) => setFormData(prev => ({ ...prev, horario_chegada: e.target.value }))}
          />
        </div>

        <div className="input-group">
          <label htmlFor="horario_saida">Horário de Saída</label>
          <input
            type="time"
            id="horario_saida"
            value={formData.horario_saida}
            onChange={(e) => setFormData(prev => ({ ...prev, horario_saida: e.target.value }))}
          />
        </div>

        <div className="input-group">
          <label htmlFor="comentario">Comentário (opcional)</label>
          <textarea
            id="comentario"
            value={formData.comentario}
            onChange={(e) => setFormData(prev => ({ ...prev, comentario: e.target.value }))}
            placeholder="Compartilhe sua experiência..."
            rows={4}
          />
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">Avaliação enviada com sucesso!</div>}

        <button 
          type="submit" 
          className="submit-button"
          disabled={submitting}
        >
          {submitting ? 'Enviando...' : 'Enviar Avaliação'}
        </button>
      </form>
    </div>
  );
};

export default FeedbackTribunal;
