// src/pages/FeedTribunais/index.jsx
import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import ContainerHome from '../../components/ContainerHome';
import { Star, StarHalf } from 'lucide-react';
import styles from './Feed.css';

const FeedTribunais = () => {
    const { id_tribunal } = useParams();
    const [comments, setComments] = useState([]);
    const [averageRating, setAverageRating] = useState(0);
    const [tribunalData, setTribunalData] = useState(null);
    const [ratingStats, setRatingStats] = useState({
        5: 0,
        4: 0,
        3: 0,
        2: 0,
        1: 0
    });

    useEffect(() => {
        if (id_tribunal) {
            fetchComments();
            fetchAverageRating();
            fetchTribunalData();
        }
    }, [id_tribunal]);

    const fetchComments = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/av_tribunais/${id_tribunal}`);
            setComments(res.data);
            calculateRatingStats(res.data);
        } catch (error) {
            console.error('Erro ao buscar comentários:', error);
        }
    };

    const calculateRatingStats = (commentsData) => {
        const stats = { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };
        commentsData.forEach(comment => {
            const rating = Math.floor(comment.avaliacao);
            if (stats[rating] !== undefined) {
                stats[rating]++;
            }
        });
        setRatingStats(stats);
    };

    const fetchAverageRating = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/tribunais_avaliacao/${id_tribunal}`);
            setAverageRating(res.data.media_avaliacao || 0);
        } catch (error) {
            console.error('Erro ao calcular média de avaliações:', error);
        }
    };

    const fetchTribunalData = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/tribunais/${id_tribunal}`);
            setTribunalData(res.data);
        } catch (error) {
            console.error('Erro ao buscar dados do tribunal:', error);
        }
    };

    const RatingBar = ({ rating, count, totalReviews }) => {
        const percentage = totalReviews > 0 ? (count / totalReviews) * 100 : 0;
        return (
            <div className="rating-bar">
                <span className="rating-number">{rating}</span>
                <div className="progress-bar">
                    <div 
                        className="progress-fill"
                        style={{ width: `${percentage}%` }}
                    />
                </div>
                <span className="rating-count">{count}</span>
            </div>
        );
    };

    const RatingStars = ({ rating }) => {
        const fullStars = Math.floor(rating);
        const hasHalfStar = rating % 1 >= 0.5;
        
        return (
            <div className={styles.ratingstars}>
                {[...Array(fullStars)].map((_, i) => (
                    <Star key={i} className={styles.starfilled} />
                ))}
                {hasHalfStar && <StarHalf className={styles.starhalf} />}
                {[...Array(5 - fullStars - (hasHalfStar ? 1 : 0))].map((_, i) => (
                    <Star key={`empty-${i}`} className={styles.starempty} />
                ))}
            </div>
        );
    };

    const totalReviews = comments.length;

    return (
        <ContainerHome>
            <div className={styles.feedcontainer}>
                {/* Header Section */}
                <div className={styles.feedheader}>
                    <Link to="/" className={styles.backbutton}>
                        ← Voltar
                    </Link>
                    {tribunalData && (
                        <h1 className={maintitle}>{tribunalData.nome}</h1>
                    )}
                </div>

                {/* Tribunal Info Section */}
                {tribunalData && (
                    <div className={styles.tribunalinfo}>
                        <div className={styles.tribunalimagecontainer}>
                            <img 
                                src={`http://localhost:3001/uploads/tribunais/${tribunalData.imagem}`} 
                                alt={tribunalData.nome} 
                                className="tribunal-image"
                            />
                        </div>
                        <p className="tribunal-description">{tribunalData.descricao}</p>
                    </div>
                )}

                {/* Ratings Overview Section */}
                <div className="ratings-card">
                    <div className="ratings-overview">
                        <div className="average-rating-container">
                            <div className="huge-rating">{averageRating.toFixed(1)}</div>
                            <RatingStars rating={averageRating} />
                            <div className="total-reviews">
                                {totalReviews.toLocaleString()} avaliações
                            </div>
                        </div>

                        <div className="rating-distribution">
                            {[5, 4, 3, 2, 1].map(rating => (
                                <RatingBar 
                                    key={rating}
                                    rating={rating}
                                    count={ratingStats[rating]}
                                    totalReviews={totalReviews}
                                />
                            ))}
                        </div>
                    </div>
                </div>

                {/* Feedback Button */}
                <div className="feedback-button-container">
                    <Link to={`/login`} className="feedback-button">
                        Realizar seu Feedback
                    </Link>
                </div>

                {/* Comments Section */}
                <div className="comments-section">
                    <h2 className="comments-title">Comentários e Avaliações</h2>
                    
                    <div className="comments-list">
                        {comments.length > 0 ? (
                            comments.map((comment, index) => (
                                <div key={index} className="comment-card">
                                    <div className="comment-header">
                                        <div className="comment-user">
                                            <div className="user-avatar">
                                                {comment.nome ? comment.nome[0].toUpperCase() : 'A'}
                                            </div>
                                            <span className="user-name">
                                                {comment.nome || `Anônimo ${comment.id_usuario}`}
                                            </span>
                                        </div>
                                        <RatingStars rating={comment.avaliacao} />
                                    </div>
                                    <p className="comment-text">{comment.comentario}</p>
                                    <div className="comment-footer">
                                        <span className="comment-date">
                                            {new Date(comment.data_criacao).toLocaleDateString('pt-BR', {
                                                year: 'numeric',
                                                month: 'long',
                                                day: 'numeric'
                                            })}
                                        </span>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <div className="no-comments">
                                <p>Ainda não há comentários para este tribunal.</p>
                                <p>Seja o primeiro a avaliar!</p>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </ContainerHome>
    );
};

export default FeedTribunais;
