import Home from "./pages/Home";
import ForunsAdmin from "./components/BD/forunsAdmin";
import ForunsList from "./pages/BDForuns";
import ForunsForm from "./pages/BDForuns";
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, BrowserRouter } from 'react-router-dom';
import TribunaisAdmin from "./components/BD/"
import TribunaisList from './pages/BDTribunais';
import TribunaisForm from './pages/BDTribunais';

import UsuariosList from './pages/BDUsuarios';
import UsuariosForm from './pages/BDUsuarios';

function App() {
  const [forumEditado, setForumEditado] = useState(null);
  const [tribunalEditado, setTribunalEditado] = useState(null);
  const [usuarioEditado, setUsuarioEditado] = useState(null);

  return (
    <BrowserRouter>
    <Route path="/" element={ <Home /> }></Route>
    <Router>
      <Routes>
        {/* Rota para Fóruns */}
        <Route
          path="/foruns"
          element={<ForunsList onEdit={setForumEditado} />}
        />
        <Route
          path="/foruns/new"
          element={<ForunsForm forumAtivo={null} onSave={() => {}} />}
        />
        <Route
          path="/foruns/edit"
          element={<ForunsForm forumAtivo={forumEditado} onSave={() => {}} onCancel={() => setForumEditado(null)} />}
        />

        {/* Rota para Tribunais */}
        <Route
          path="/tribunais"
          element={<TribunaisList onEdit={setTribunalEditado} />}
        />
        <Route
          path="/tribunais/new"
          element={<TribunaisForm tribunalAtivo={null} onSave={() => {}} />}
        />
        <Route
          path="/tribunais/edit"
          element={<TribunaisForm tribunalAtivo={tribunalEditado} onSave={() => {}} onCancel={() => setTribunalEditado(null)} />}
        />

        {/* Rota para Usuários */}
        <Route
          path="/usuarios"
          element={<UsuariosList onEdit={setUsuarioEditado} />}
        />
        <Route
          path="/usuarios/new"
          element={<UsuariosForm usuarioAtivo={null} onSave={() => {}} />}
        />
        <Route
          path="/usuarios/edit"
          element={<UsuariosForm usuarioAtivo={usuarioEditado} onSave={() => {}} onCancel={() => setUsuarioEditado(null)} />}
        />
      </Routes>
    </Router>
    </BrowserRouter>
  );
}

export default App;
