import React, { useState, useEffect } from 'react';
import CarouselCustom from '../../components/Carrosel/CarouselCustom';
import axios from 'axios';

function HomeCards() {
    const [carouselItems, setCarouselItems] = useState([]);

    const getImageUrl = (type, imagem) => {
        if (!imagem) return null;
        if (imagem.startsWith('/uploads/')) {
            return `http://localhost:3001${imagem}`;
        }
        return `http://localhost:3001/uploads/${type}/${imagem}`;
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await axios.get('http://localhost:3001/tribunais');
                const entidadesWithRatings = await Promise.all(
                    res.data.map(async (entidade) => {
                        try {
                            const ratingRes = await axios.get(`http://localhost:3001/avaliacoes/${entidade.id}`);
                            const mediaAvaliacao = parseFloat(ratingRes.data.media_avaliacao) || 0;
                            
                            return {
                                title: entidade.nome,
                                subtitle: `Avalia√ß√£o: ${mediaAvaliacao.toFixed(1)} ‚≠ê`,
                                image: getImageUrl('estabelecimentos', entidade.imagem),
                                link: `/estabelecimento/${entidade.id}`,
                            };
                        } catch (err) {
                            console.error(`Erro ao buscar avalia√ß√£o:`, err);
                            return {
                                title: entidade.nome,
                                subtitle: 'Sem avalia√ß√µes',
                                image: getImageUrl('estabelecimentos', entidade.imagem),
                                link: `/estabelecimento/${entidade.id}`,
                            };
                        }
                    })
                );
                setCarouselItems(entidadesWithRatings);
            } catch (err) {
                console.error('Erro ao listar estabelecimentos:', err);
            }
        };

        fetchData();
    }, []);

    return (
        <div className="App">
            <CarouselCustom 
                name="tribunais" 
                itemsPerSlide={3} 
                carouselItems={carouselItems} 
            />
        </div>
    );
}

