import React, { useState } from 'react';
import { Star } from 'lucide-react';
import axios from 'axios';

const FeedbackTribunal = ({ id_tribunal, id_usuario }) => {
  const [formData, setFormData] = useState({
    numero_protocolo: '',
    comentario: '',
    av_eficiencia: 0,
    av_qualidade: 0,
    av_infraestrutura: 0,
    av_tecnologia: 0,
    av_gestao: 0,
    av_transparencia: 0,
    av_sustentabilidade: 0,
    horario_chegada: '',
    horario_saida: ''
  });

  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const categorias = [
    { key: 'av_eficiencia', label: 'Eficiência Operacional', desc: 'Avalie o tempo de tramitação e produtividade' },
    { key: 'av_qualidade', label: 'Qualidade do Serviço', desc: 'Avalie a qualidade do atendimento prestado' },
    { key: 'av_infraestrutura', label: 'Infraestrutura', desc: 'Avalie as instalações físicas' },
    { key: 'av_tecnologia', label: 'Tecnologia', desc: 'Avalie os sistemas eletrônicos disponíveis' },
    { key: 'av_gestao', label: 'Gestão', desc: 'Avalie a organização e gestão do tribunal' },
    { key: 'av_transparencia', label: 'Transparência', desc: 'Avalie o acesso à informação' },
    { key: 'av_sustentabilidade', label: 'Sustentabilidade', desc: 'Avalie as práticas sustentáveis' }
  ];

  const handleRatingChange = (categoria, valor) => {
    setFormData(prev => ({
      ...prev,
      [categoria]: valor
    }));
  };

  const renderStars = (categoria) => {
    const rating = formData[categoria];
    return (
      <div className="stars-container">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            type="button"
            className="star-button"
            onClick={() => handleRatingChange(categoria, star)}
          >
            <Star 
              className={`star ${star <= rating ? 'filled' : ''}`}
              fill={star <= rating ? '#4169E1' : 'none'}
              stroke={star <= rating ? '#4169E1' : '#dadce0'}
            />
          </button>
        ))}
      </div>
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');

    try {
      const response = await axios.post('http://localhost:3001/av_tribunais', {
        ...formData,
        id_tribunal,
        id_usuario
      });

      setSuccess(true);
      setTimeout(() => {
        setSuccess(false);
        setFormData({
          numero_protocolo: '',
          comentario: '',
          av_eficiencia: 0,
          av_qualidade: 0,
          av_infraestrutura: 0,
          av_tecnologia: 0,
          av_gestao: 0,
          av_transparencia: 0,
          av_sustentabilidade: 0,
          horario_chegada: '',
          horario_saida: ''
        });
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.error || 'Erro ao enviar avaliação');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm max-w-3xl mx-auto my-8 p-8">
      <h2 className="text-2xl text-gray-900 mb-8 text-center">Avalie sua experiência</h2>
      
      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="flex flex-col gap-2">
          <label htmlFor="numero_protocolo" className="text-sm text-gray-600 font-medium">
            Número do Protocolo
          </label>
          <input
            type="text"
            id="numero_protocolo"
            value={formData.numero_protocolo}
            onChange={(e) => setFormData(prev => ({ ...prev, numero_protocolo: e.target.value }))}
            required
            minLength={5}
            maxLength={20}
            placeholder="Digite o número do protocolo"
            className="px-3 py-2 border border-gray-300 rounded-md focus:border-blue-500 focus:outline-none"
          />
        </div>

        {categorias.map(({ key, label, desc }) => (
          <div key={key} className="p-4 bg-gray-50 rounded-lg">
            <div className="mb-2">
              <h3 className="text-base text-gray-900 font-medium">{label}</h3>
              <p className="text-sm text-gray-600">{desc}</p>
            </div>
            <div className="flex gap-2 mt-2">
              {renderStars(key)}
            </div>
          </div>
        ))}

        <div className="flex flex-col gap-2">
          <label htmlFor="horario_chegada" className="text-sm text-gray-600 font-medium">
            Horário de Chegada
          </label>
          <input
            type="time"
            id="horario_chegada"
            value={formData.horario_chegada}
            onChange={(e) => setFormData(prev => ({ ...prev, horario_chegada: e.target.value }))}
            className="px-3 py-2 border border-gray-300 rounded-md focus:border-blue-500 focus:outline-none"
          />
        </div>

        <div className="flex flex-col gap-2">
          <label htmlFor="horario_saida" className="text-sm text-gray-600 font-medium">
            Horário de Saída
          </label>
          <input
            type="time"
            id="horario_saida"
            value={formData.horario_saida}
            onChange={(e) => setFormData(prev => ({ ...prev, horario_saida: e.target.value }))}
            className="px-3 py-2 border border-gray-300 rounded-md focus:border-blue-500 focus:outline-none"
          />
        </div>

        <div className="flex flex-col gap-2">
          <label htmlFor="comentario" className="text-sm text-gray-600 font-medium">
            Comentário (opcional)
          </label>
          <textarea
            id="comentario"
            value={formData.comentario}
            onChange={(e) => setFormData(prev => ({ ...prev, comentario: e.target.value }))}
            placeholder="Compartilhe sua experiência..."
            rows={4}
            className="px-3 py-2 border border-gray-300 rounded-md focus:border-blue-500 focus:outline-none"
          />
        </div>

        {error && (
          <div className="p-3 bg-red-50 text-red-700 rounded-md text-sm">
            {error}
          </div>
        )}
        
        {success && (
          <div className="p-3 bg-green-50 text-green-700 rounded-md text-sm">
            Avaliação enviada com sucesso!
          </div>
        )}

        <button 
          type="submit" 
          className="w-full bg-blue-600 text-white py-3 px-6 rounded-md font-medium hover:bg-blue-700 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors"
          disabled={submitting}
        >
          {submitting ? 'Enviando...' : 'Enviar Avaliação'}
        </button>
      </form>
    </div>
  );
};

export default FeedbackTribunal;
