import React, { useState, useEffect } from 'react';
import './HeaderLggd.css';

const HeaderLggd = ({ user, role }) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [progress, setProgress] = useState(0);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const generateToken = () => {
    setProgress(0); // Reset progress to 0 when generating a token
    // Adicionar lógica para gerar o token aqui
  };

  useEffect(() => {
    // Simula progressão de pontos (você pode ajustar a lógica)
    const timer = setInterval(() => {
      setProgress((prev) => (prev < 1000 ? prev + 50 : 1000));
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <header className="headerLggd">
      <div className="brand">Justix</div>
      <button onClick={toggleSidebar} className="userButton">Dados do usuário</button>
      {isSidebarOpen && (
        <div className="sidebar">
          <button className="closeSidebar" onClick={toggleSidebar}>×</button>
          <div className="sidebarContent">
            {role === 'admin' ? (
              <>
                <p>Nome: {user.name}</p>
                <button onClick={generateToken}>Logout</button>
              </>
            ) : (
              <>
                <p>Nome: {user.name}</p>
                <p>CPF: {user.cpf}</p>
                <p>Telefone: {user.phone}</p>
                <p>Email: {user.email}</p>
                <button onClick={generateToken}>Gerar Token</button>
              </>
            )}
            <div className="progressContainer">
              <label>Progresso:</label>
              <div className="progressBar">
                <div className="progress" style={{ width: `${(progress / 1000) * 100}%` }}>
                  {progress} / 1000
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
      {/* Overlay for mobile view */}
      {isSidebarOpen && <div className="sidebarOverlay" onClick={toggleSidebar}></div>}
    </header>
  );
};

export default HeaderLggd;
