import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CarouselCustom = ({ name, itemsPerSlide = 3, endpoint, ratingEndpoint }) => {
  const [items, setItems] = useState([]);
  
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/${endpoint}`);
        const entidadesWithRatings = await Promise.all(
          res.data.map(async (entidade) => {
            if (ratingEndpoint) {
              try {
                const ratingRes = await axios.get(`http://localhost:3001/${ratingEndpoint}/${entidade.id}`);
                const mediaAvaliacao = parseFloat(ratingRes.data.media_avaliacao) || 0;
                return {
                  ...entidade,
                  avaliacao_media: mediaAvaliacao
                };
              } catch (err) {
                console.error(`Erro ao buscar avaliação:`, err);
                return {
                  ...entidade,
                  avaliacao_media: 0
                };
              }
            }
            return entidade;
          })
        );
        setItems(entidadesWithRatings);
      } catch (err) {
        console.error(`Erro ao listar itens:`, err);
      }
    };

    fetchData();
  }, [endpoint, ratingEndpoint]);

  // Função para criar grupos de itens baseado no itemsPerSlide
  const createItemGroups = () => {
    const groups = [];
    for (let i = 0; i < items.length; i += itemsPerSlide) {
      groups.push(items.slice(i, i + itemsPerSlide));
    }
    return groups;
  };

  // Função para obter URL da imagem
  const getImageUrl = (type, imagem) => {
    if (!imagem) return null;
    if (imagem.startsWith('/uploads/')) {
      return `http://localhost:3001${imagem}`;
    }
    return `http://localhost:3001/uploads/${type}/${imagem}`;
  };

  return (
    <div className="container mt-4">
      {name && <h2 className="mb-4">{name}</h2>}
      
      <div id="mainCarousel" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner">
          {createItemGroups().map((group, groupIndex) => (
            <div className={`carousel-item ${groupIndex === 0 ? 'active' : ''}`} key={groupIndex}>
              <div className="row">
                {group.map((item, index) => (
                  <div className="col-md-4" key={item.id}>
                    <div className="card h-100">
                      <img 
                        src={getImageUrl(endpoint, item.imagem)} 
                        className="card-img-top" 
                        alt={item.nome}
                        style={{ height: '200px', objectFit: 'cover' }}
                      />
                      <div className="card-body">
                        <h5 className="card-title">{item.nome}</h5>
                        <p className="card-text">
                          {item.descricao && item.descricao.substring(0, 100)}...
                        </p>
                        {item.avaliacao_media !== undefined && (
                          <p className="card-text">
                            <small className="text-muted">
                              Avaliação: {item.avaliacao_media.toFixed(1)} ⭐
                            </small>
                          </p>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
        
        <button className="carousel-control-prev" type="button" data-bs-target="#mainCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Anterior</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#mainCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Próximo</span>
        </button>
      </div>
    </div>
  );
};

export default CarouselCustom;
