import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ForunsListFeed = () => {
    const { id_forum } = useParams(); // Obtém o ID do fórum dos parâmetros da URL
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [rating, setRating] = useState(1);
    const [averageRating, setAverageRating] = useState(0);
    const [protocolNumber, setProtocolNumber] = useState('');
    const [arrivalTime, setArrivalTime] = useState('');
    const [departureTime, setDepartureTime] = useState('');

    useEffect(() => {
        fetchComments();
        fetchAverageRating();
    }, [id_forum]);

    const fetchComments = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/av_foruns/${id_forum}`);
            setComments(res.data);
        } catch (error) {
            console.error('Erro ao buscar comentários:', error);
        }
    };

    const fetchAverageRating = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/foruns/${id_forum}/avaliacao`);
            setAverageRating(res.data.media_avaliacao || 0);
        } catch (error) {
            console.error('Erro ao calcular média de avaliações:', error);
        }
    };

    const handleSubmitComment = async (e) => {
        e.preventDefault();

        if (protocolNumber.length < 5 || protocolNumber.length > 20) {
            alert('O número de protocolo deve ter entre 5 e 20 dígitos.');
            return;
        }

        const id_usuario = localStorage.getItem('id_usuario'); // Obtém o ID do usuário do localStorage

        try {
            await axios.post('http://localhost:3001/av_foruns', {
                id_usuario,
                id_forum,
                numero_protocolo: protocolNumber,
                comentario: newComment,
                avaliacao: rating,
                horario_chegada: arrivalTime || null,
                horario_saida: departureTime || null,
            });
            setNewComment('');
            setRating(1);
            setProtocolNumber('');
            setArrivalTime('');
            setDepartureTime('');
            fetchComments();
            fetchAverageRating();
        } catch (error) {
            console.error('Erro ao adicionar comentário:', error);
        }
    };

    return (
        <div>
            <h2>Comentários do Fórum</h2>
            <h3>Média de Avaliação: ★ {averageRating.toFixed(1)}</h3>

            <div>
                <h4>Deixe seu Comentário</h4>
                <form onSubmit={handleSubmitComment}>
                    <label>
                        Número de Protocolo:
                        <input
                            type="text"
                            value={protocolNumber}
                            onChange={(e) => setProtocolNumber(e.target.value)}
                            required
                            minLength="5"
                            maxLength="20"
                        />
                    </label>
                    <label>
                        Comentário:
                        <textarea
                            value={newComment}
                            onChange={(e) => setNewComment(e.target.value)}
                        />
                    </label>
                    <label>
                        Avaliação:
                        <select value={rating} onChange={(e) => setRating(e.target.value)}>
                            {[1, 2, 3, 4, 5].map((star) => (
                                <option key={star} value={star}>
                                    {star}
                                </option>
                            ))}
                        </select>
                    </label>
                    <label>
                        Horário de Chegada (Opcional):
                        <input
                            type="time"
                            value={arrivalTime}
                            onChange={(e) => setArrivalTime(e.target.value)}
                        />
                    </label>
                    <label>
                        Horário de Saída (Opcional):
                        <input
                            type="time"
                            value={departureTime}
                            onChange={(e) => setDepartureTime(e.target.value)}
                        />
                    </label>
                    <button type="submit">Enviar Comentário</button>
                </form>
            </div>

            <div>
                <h4>Comentários</h4>
                {comments.length > 0 ? (
                    comments.map((comment, index) => (
                        <div key={index}>
                            <p><strong>{comment.nome}</strong> - Avaliação: ★ {comment.avaliacao}</p>
                            <p>{comment.comentario}</p>
                            <p>Data: {new Date(comment.data_criacao).toLocaleDateString()}</p>
                        </div>
                    ))
                ) : (
                    <p>Sem comentários para este fórum.</p>
                )}
            </div>
        </div>
    );
};

export default ForunsListFeed;
