import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Search, Star } from 'lucide-react';


const TribunaisListPageUser = () => {
  const [tribunais, setTribunais] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [activeCategory, setActiveCategory] = useState('all');

  useEffect(() => {
    listarTribunais();
  }, []);

  const listarTribunais = async () => {
    try {
      const res = await axios.get('http://localhost:3001/tribunais');
      const tribunaisWithRatings = await Promise.all(
        res.data.map(async (tribunal) => {
          try {
            const ratingRes = await axios.get(`http://localhost:3001/tribunais_avaliacao/${tribunal.id_tribunal}`);
            return {
              ...tribunal,
              avaliacao_media: parseFloat(ratingRes.data.media_avaliacao) || 0,
              total_avaliacoes: Math.floor(Math.random() * 10000), // Simulated data
            };
          } catch (err) {
            console.error(`Erro ao buscar avaliação para tribunal ${tribunal.id_tribunal}:`, err);
            return {
              ...tribunal,
              avaliacao_media: 0,
              total_avaliacoes: 0,
            };
          }
        })
      );
      setTribunais(tribunaisWithRatings);
    } catch (err) {
      console.error('Erro ao listar tribunais:', err);
    }
  };

  const getImagemUrl = (imagem) => {
    if (!imagem) return null;
    if (imagem.startsWith('/uploads/tribunais/')) {
      return `http://localhost:3001${imagem}`;
    }
    return `http://localhost:3001/uploads/tribunais/${imagem}`;
  };

  const handleVisualizarClick = (id_tribunal) => {
    localStorage.setItem('id_tribunal', id_tribunal);
  };

  const filteredTribunais = tribunais.filter(tribunal => 
    tribunal.nome.toLowerCase().includes(searchTerm.toLowerCase()) || 
    tribunal.endereco.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const formatRating = (rating) => {
    const numRating = parseFloat(rating);
    return isNaN(numRating) ? "0.0" : numRating.toFixed(1);
  };

  const formatTotalAvaliacoes = (total) => {
    if (total >= 1000000) return `${(total / 1000000).toFixed(1)}M`;
    if (total >= 1000) return `${(total / 1000).toFixed(1)}K`;
    return total.toString();
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      {/* Header com Search */}
      <div className="bg-white sticky top-0 z-10 shadow-sm">
        <div className="max-w-7xl mx-auto p-4">
          <div className="relative">
            <input
              type="text"
              placeholder="Pesquisar tribunais"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full p-3 pl-12 bg-gray-100 rounded-full focus:outline-none focus:ring-2 focus:ring-green-500"
            />
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-500" size={20} />
          </div>
        </div>

        {/* Categorias */}
        <div className="border-b">
          <div className="max-w-7xl mx-auto px-4 overflow-x-auto">
            <div className="flex space-x-6 py-3">
              {['Todos', 'Mais Avaliados', 'Melhor Classificação', 'Recentes'].map((category) => (
                <button
                  key={category}
                  onClick={() => setActiveCategory(category.toLowerCase())}
                  className={`whitespace-nowrap px-2 py-1 text-sm font-medium ${
                    activeCategory === category.toLowerCase()
                      ? 'text-green-600 border-b-2 border-green-600'
                      : 'text-gray-600'
                  }`}
                >
                  {category}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Lista de Tribunais */}
      <div className="max-w-7xl mx-auto p-4">
        <h2 className="text-xl font-medium text-gray-900 mb-4">Tribunais Recomendados</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {filteredTribunais.map(tribunal => (
            <Link
              key={tribunal.id_tribunal}
              to={`/user/dashboard/tribunais/${tribunal.id_tribunal}/feedback`}
              onClick={() => handleVisualizarClick(tribunal.id_tribunal)}
              className="bg-white rounded-xl p-4 hover:shadow-md transition-shadow"
            >
              <div className="flex items-center mb-4">
                <div className="w-16 h-16 rounded-xl overflow-hidden bg-gray-100 flex-shrink-0">
                  {tribunal.imagem ? (
                    <img
                      src={getImagemUrl(tribunal.imagem)}
                      alt={tribunal.nome}
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <div className="w-full h-full flex items-center justify-center text-gray-400 text-xs">
                      Sem imagem
                    </div>
                  )}
                </div>
                <div className="ml-4 flex-grow">
                  <h3 className="font-medium text-gray-900 line-clamp-2">{tribunal.nome}</h3>
                  <p className="text-sm text-gray-500 mt-1">{tribunal.cidade}</p>
                  <div className="flex items-center mt-1">
                    <Star className="w-4 h-4 text-yellow-400 fill-current" />
                    <span className="text-sm text-gray-600 ml-1">
                      {formatRating(tribunal.avaliacao_media)}
                    </span>
                    <span className="text-sm text-gray-400 ml-1">
                      ({formatTotalAvaliacoes(tribunal.total_avaliacoes)})
                    </span>
                  </div>
                </div>
              </div>
              <div className="text-xs text-gray-500 line-clamp-2">
                {tribunal.endereco}, {tribunal.estado}, {tribunal.cep}
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TribunaisListPageUser;
