import React, { useState } from 'react';
import styles from "./ContainerLggd.module.css";
import { useLocation } from 'react-router-dom';

const ContainerLggd = ({ children, onSearch }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const location = useLocation();
    
    // Pega o título da URL e capitaliza
    const title = location.pathname.substring(1) // Remove a barra inicial
                    .split('/').pop() // Pega o último segmento
                    .split('-').join(' ') // Substitui hífens por espaços
                    .replace(/\b\w/g, (l) => l.toUpperCase()); // Capitaliza cada palavra
    
    const handleSearch = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
        if (onSearch) {
            onSearch(value);
        }
    };

    return (
        <section>
            <div className="{styles.container}">
                <div className={styles.searchpad}>
                    <input
                        type="text"
                        placeholder="Pesquisar..."
                        value={searchTerm}
                        onChange={handleSearch}
                        className={styles.searchpad}
                    />
                </div>
                <div className={styles.titulo}>{title}</div>
                <div className="w-96"></div>
            </div>
            <main className="p-6">
                {children}
            </main>
        </section>
    );
};

export default ContainerLggd;
