// components/GooglePlayLayout.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Star, StarHalf } from 'lucide-react';
import axios from 'axios';
import styles from './TribunaisListPageUser.css';

const API_BASE_URL = 'http://localhost:3001';

const sections = [
  { id: 'tribunais', name: 'Tribunais' },
  { id: 'foruns', name: 'Fóruns' },
  { id: 'juiz', name: 'Audiências' },
  { id: 'mediador', name: 'Mediações' },
  { id: 'advocacia', name: 'Advocacia' },
  { id: 'portais', name: 'Portais' }
];

const RatingStars = ({ rating }) => {
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 >= 0.5;
  const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

  return (
    <div className={styles.stars}>
      {[...Array(fullStars)].map((_, i) => (
        <Star key={`full-${i}`} size={16} fill="#fbbc04" stroke="#fbbc04" />
      ))}
      {hasHalfStar && <StarHalf size={16} fill="#fbbc04" stroke="#fbbc04" />}
      {[...Array(emptyStars)].map((_, i) => (
        <Star key={`empty-${i}`} size={16} stroke="#fbbc04" />
      ))}
    </div>
  );
};

const ItemCard = ({ item, type }) => {
  const getImageUrl = (image) => {
    if (!image) return null;
    if (image.startsWith('/uploads/')) {
      return `${API_BASE_URL}${image}`;
    }
    return `${API_BASE_URL}/uploads/${type}/${image}`;
  };

  return (
    <Link to={`/${type}/${item.id}/feedback`} className={styles.card}>
      {item.imagem ? (
        <img
          src={getImageUrl(item.imagem)}
          alt={item.nome}
          className={styles.cardImage}
        />
      ) : (
        <div className={styles.noImage}>Sem imagem</div>
      )}
      <div className={styles.cardContent}>
        <h3 className={styles.cardTitle}>{item.nome}</h3>
        <p className={styles.cardSubtitle}>
          {item.profissao || item.descricao || ''}
        </p>
        <div className={styles.rating}>
          <RatingStars rating={item.media_ponderada || 0} />
          <span className={styles.ratingScore}>
            {(item.media_ponderada || 0).toFixed(1)}
          </span>
        </div>
      </div>
    </Link>
  );
};

const Section = ({ title, items, type }) => (
  <section className={styles.section}>
    <div className={styles.sectionHeader}>
      <h2 className={styles.sectionTitle}>{title}</h2>
      <Link to={`/${type}`} className={styles.seeMore}>
        Ver mais
      </Link>
    </div>
    <div className={styles.itemsGrid}>
      {items.map((item) => (
        <ItemCard
          key={item[`id_${type}`]}
          item={item}
          type={type}
        />
      ))}
    </div>
  </section>
);

const Navigation = ({ currentSection, onSectionChange }) => (
  <nav className={styles.nav}>
    <div className={styles.container}>
      <ul className={styles.navList}>
        {sections.map((section) => (
          <li
            key={section.id}
            className={`${styles.navItem} ${
              currentSection === section.id ? styles.active : ''
            }`}
            onClick={() => onSectionChange(section.id)}
          >
            {section.name}
          </li>
        ))}
      </ul>
    </div>
  </nav>
);

const TribunaisListPageUser = () => {
  const [currentSection, setCurrentSection] = useState('tribunais');
  const [sectionData, setSectionData] = useState([]);

  const fetchDataWithRatings = async (section) => {
    try {
      const res = await axios.get(`${API_BASE_URL}/${section}`);
      const items = res.data;

      const itemsWithRatings = await Promise.all(
        items.map(async (item) => {
          try {
            const ratingRes = await axios.get(
              `${API_BASE_URL}/${section}_avaliacao/${
                item[`id_${section.slice(0, -1)}`]
              }`
            );
            return {
              ...item,
              media_ponderada: parseFloat(ratingRes.data.media_ponderada) || 0
            };
          } catch (err) {
            return { ...item, media_ponderada: 0 };
          }
        })
      );

      setSectionData(itemsWithRatings);
    } catch (error) {
      console.error(`Error fetching ${section}:`, error);
      setSectionData([]);
    }
  };

  useEffect(() => {
    fetchDataWithRatings(currentSection);
  }, [currentSection]);

  return (
    <div>
      <Navigation
        currentSection={currentSection}
        onSectionChange={setCurrentSection}
      />
      <main className={styles.container}>
        <Section
          title={sections.find(s => s.id === currentSection).name}
          items={sectionData}
          type={currentSection}
        />
      </main>
    </div>
  );
};

export default TribunaisListPageUser;
