import React, { useState, useEffect } from 'react';
import styles from "./Header.module.css";

const Header = ({ children, carouselItems }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [filteredItems, setFilteredItems] = useState([]);

  useEffect(() => {
    // Filtrar itens conforme o searchQuery
    if (searchQuery) {
      const filtered = carouselItems.filter(item =>
        item.title.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredItems(filtered);
    } else {
      setFilteredItems(carouselItems);
    }
  }, [searchQuery, carouselItems]);

  const handleSearch = (e) => {
    e.preventDefault();
    console.log('Searching for:', searchQuery);
  };

  const handleFocus = () => {
    setIsMenuOpen(true);
  };

  const handleBlur = () => {
    setIsMenuOpen(false);
  };

  return (
    <header>
      <nav className={styles.navbar}>
        <span className={styles.brand}>JUSTIX</span>
        <div className={styles.searchContainer}>
          <form onSubmit={handleSearch} className={styles.searchForm}>
            <div className={styles.searchWrapper}>
              <input
                type="text"
                placeholder="Pesquisar..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onFocus={handleFocus}
                className={styles.searchInput}
              />
              <button type="submit" className={styles.searchButton}>
                Buscar
              </button>
            </div>
          </form>

          {/* Mega menu */}
          {isMenuOpen && (
            <div className={styles.megaMenu}>
              {filteredItems.length > 0 ? (
                filteredItems.map((item) => (
                  <div key={item.id} className={styles.menuItem}>
                    <a href={item.link}>{item.title}</a>
                  </div>
                ))
              ) : (
                <div className={styles.noResults}>Nenhum item encontrado</div>
              )}
            </div>
          )}
        </div>
        
        {children}
        
      </nav>
    </header>
  );
};

export default Header;
