import React, { useState, useEffect } from 'react';
import CarouselCustom from '../../components/Carrosel/CarouselCustom';
import axios from 'axios';

function HomeCards() {
    const [tribunais, setTribunais] = useState([]);
    const [foruns, setForuns] = useState([]);
    const [juiz, setJuizes] = useState([]);
    const [mediador, setMediadores] = useState([]);
    const [advocacia, setAdvocacia] = useState([]);
    const [portais, setPortais] = useState([]);

    const getImageUrl = (type, imagem) => {
        if (!imagem) return null;
        if (imagem.startsWith('/uploads/')) {
            return `http://localhost:3001${imagem}`;
        }
        return `http://localhost:3001/uploads/${type}/${imagem}`;
    };

    const fetchDataWithRatings = async (endpoint, setStateFunc, ratingEndpoint, idField) => {
        try {
            const res = await axios.get(`http://localhost:3001/${endpoint}`);
            const entidadesWithRatings = await Promise.all(
                res.data.map(async (entidade) => {
                    try {
                        const ratingRes = await axios.get(`http://localhost:3001/${ratingEndpoint}/${entidade[idField]}`);
                        const mediaAvaliacao = parseFloat(ratingRes.data.media_avaliacao) || 0;
                        
                        return {
                            title: entidade.nome,
                            subtitle: `Avalia√ß√£o: ${mediaAvaliacao.toFixed(1)} ‚≠ê`,
                            image: getImageUrl(endpoint, entidade.imagem),
                            link: `/${endpoint}/${entidade[idField]}/feedback`,
                            id: entidade[idField],
                            avaliacao_media: mediaAvaliacao
                        };
                    } catch (err) {
                        console.error(`Erro ao buscar avalia√ß√£o:`, err);
                        return {
                            title: entidade.nome,
                            subtitle: 'Sem avalia√ß√µes',
                            image: getImageUrl(endpoint, entidade.imagem),
                            link: `/${endpoint}/${entidade[idField]}/feedback`,
                            id: entidade[idField],
                            avaliacao_media: 0
                        };
                    }
                })
            );
            setStateFunc(entidadesWithRatings);
        } catch (err) {
            console.error(`Erro ao listar ${endpoint}:`, err);
        }
    };

    useEffect(() => {
        fetchDataWithRatings('tribunais', setTribunais, 'tribunais_avaliacao', 'id_tribunal');
        fetchDataWithRatings('foruns', setForuns, 'foruns_avaliacao', 'id_forum');
        fetchDataWithRatings('juizes', setJuizes, 'juizes_avaliacao', 'id_juiz');
        fetchDataWithRatings('mediadores', setMediadores, 'mediadores_avaliacao', 'id_mediador');
        fetchDataWithRatings('advocacias', setAdvocacia, 'advocacias_avaliacao', 'id_advocacia');
        fetchDataWithRatings('portais', setPortais, 'portais_avaliacao', 'id_portal');
    }, []);

    return (
        <div className="App">
            {tribunais.length > 0 && (
                <CarouselCustom 
                    name="Tribunais"
                    carouselItems={tribunais}
                />
            )}
            {foruns.length > 0 && (
                <CarouselCustom 
                    name="F√≥runs"
                    carouselItems={foruns}
                />
            )}
            {juiz.length > 0 && (
                <CarouselCustom 
                    name="Ju√≠zes"
                    carouselItems={juiz}
                />
            )}
            {mediador.length > 0 && (
                <CarouselCustom 
                    name="Mediadores"
                    carouselItems={mediador}
                />
            )}
            {advocacia.length > 0 && (
                <CarouselCustom 
                    name="Advocacias"
                    carouselItems={advocacia}
                />
            )}
            {portais.length > 0 && (
                <CarouselCustom 
                    name="Portais"
                    carouselItems={portais}
                />
            )}
        </div>
    );
}

export default HomeCards;
