import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const ForunsListFeed = () => {
    const { id_forum } = useParams();
    const navigate = useNavigate();
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [rating, setRating] = useState(1);
    const [averageRating, setAverageRating] = useState(0);
    const [protocolNumber, setProtocolNumber] = useState('');
    const [arrivalTime, setArrivalTime] = useState('');
    const [departureTime, setDepartureTime] = useState('');

    useEffect(() => {
        // Verificar se o usuário está logado
        const id_usuario = localStorage.getItem('id_usuario');
        if (!id_usuario) {
            alert('Você precisa estar logado para acessar esta página');
            navigate('/login'); // Redireciona para a página de login
            return;
        }

        fetchComments();
        fetchAverageRating();
    }, [id_forum, navigate]);

    const handleSubmitComment = async (e) => {
        e.preventDefault();

        const id_usuario = localStorage.getItem('id_usuario');
        
        // Verificação adicional de segurança
        if (!id_usuario) {
            alert('Você precisa estar logado para comentar');
            navigate('/login');
            return;
        }

        if (protocolNumber.length < 5 || protocolNumber.length > 20) {
            alert('O número de protocolo deve ter entre 5 e 20 dígitos.');
            return;
        }

        try {
            const commentData = {
                id_usuario: parseInt(id_usuario), // Converte para número
                id_forum: parseInt(id_forum),    // Converte para número
                numero_protocolo: protocolNumber,
                comentario: newComment,
                avaliacao: parseInt(rating),     // Converte para número
                horario_chegada: arrivalTime || null,
                horario_saida: departureTime || null,
            };

            // Log para debug
            console.log('Dados sendo enviados:', commentData);

            const response = await axios.post('http://localhost:3001/av_foruns', commentData);
            
            if (response.status === 201) {
                setNewComment('');
                setRating(1);
                setProtocolNumber('');
                setArrivalTime('');
                setDepartureTime('');
                fetchComments();
                fetchAverageRating();
            }
        } catch (error) {
            console.error('Erro ao adicionar comentário:', error.response?.data || error.message);
            alert('Erro ao adicionar comentário. Por favor, tente novamente.');
        }
    };

    // ... resto do seu código (fetchComments, fetchAverageRating, etc) ...

    return (
        <div className="p-4">
            <h2 className="text-2xl font-bold mb-4">Comentários do Fórum</h2>
            <h3 className="text-xl mb-4">Média de Avaliação: ★ {averageRating ? averageRating.toFixed(1) : '0.0'}</h3>

            <div className="mb-8">
                <h4 className="text-lg font-semibold mb-4">Deixe seu Comentário</h4>
                <form onSubmit={handleSubmitComment} className="space-y-4">
                    <div>
                        <label className="block mb-2">
                            Número de Protocolo:
                            <input
                                type="text"
                                value={protocolNumber}
                                onChange={(e) => setProtocolNumber(e.target.value)}
                                required
                                minLength="5"
                                maxLength="20"
                                className="w-full p-2 border rounded"
                            />
                        </label>
                    </div>
                    
                    <div>
                        <label className="block mb-2">
                            Comentário:
                            <textarea
                                value={newComment}
                                onChange={(e) => setNewComment(e.target.value)}
                                className="w-full p-2 border rounded"
                                rows="4"
                            />
                        </label>
                    </div>

                    <div>
                        <label className="block mb-2">
                            Avaliação:
                            <select 
                                value={rating} 
                                onChange={(e) => setRating(e.target.value)}
                                className="w-full p-2 border rounded"
                            >
                                {[1, 2, 3, 4, 5].map((star) => (
                                    <option key={star} value={star}>
                                        {'★'.repeat(star)} {star}
                                    </option>
                                ))}
                            </select>
                        </label>
                    </div>

                    <div className="flex gap-4">
                        <label className="block flex-1">
                            Horário de Chegada:
                            <input
                                type="time"
                                value={arrivalTime}
                                onChange={(e) => setArrivalTime(e.target.value)}
                                className="w-full p-2 border rounded"
                            />
                        </label>

                        <label className="block flex-1">
                            Horário de Saída:
                            <input
                                type="time"
                                value={departureTime}
                                onChange={(e) => setDepartureTime(e.target.value)}
                                className="w-full p-2 border rounded"
                            />
                        </label>
                    </div>

                    <button 
                        type="submit" 
                        className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
                    >
                        Enviar Comentário
                    </button>
                </form>
            </div>

            <div>
                <h4 className="text-lg font-semibold mb-4">Comentários</h4>
                {comments.length > 0 ? (
                    <div className="space-y-4">
                        {comments.map((comment, index) => (
                            <div key={index} className="p-4 border rounded">
                                <div className="flex justify-between items-center mb-2">
                                    <strong>{comment.nome}</strong>
                                    <span>Avaliação: {'★'.repeat(comment.avaliacao)}</span>
                                </div>
                                <p className="mb-2">{comment.comentario}</p>
                                <p className="text-sm text-gray-600">
                                    Data: {new Date(comment.data_criacao).toLocaleDateString()}
                                </p>
                            </div>
                        ))}
                    </div>
                ) : (
                    <p className="text-gray-600">Sem comentários para este fórum.</p>
                )}
            </div>
        </div>
    );
};

export default ForunsListFeed;
