import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import ContainerHome from '../../../components/ContainerHome/index';
import { Star, StarHalf } from 'lucide-react';
i

const FeedTribunais = () => {
    const { id_tribunal } = useParams();
    const [comments, setComments] = useState([]);
    const [averageRating, setAverageRating] = useState(0);
    const [tribunalData, setTribunalData] = useState(null);
    const [ratingStats, setRatingStats] = useState({
        5: 0,
        4: 0,
        3: 0,
        2: 0,
        1: 0
    });

    useEffect(() => {
        if (id_tribunal) {
            fetchComments();
            fetchAverageRating();
            fetchTribunalData();
        }
    }, [id_tribunal]);

    const fetchComments = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/av_tribunais/${id_tribunal}`);
            setComments(res.data);
            // Calculate rating distribution
            const stats = { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };
            res.data.forEach(comment => {
                stats[Math.floor(comment.avaliacao)]++;
            });
            setRatingStats(stats);
        } catch (error) {
            console.error('Erro ao buscar comentários:', error);
        }
    };

    // ... rest of your fetch functions ...

    const RatingBar = ({ rating, count, totalReviews }) => {
        const percentage = totalReviews > 0 ? (count / totalReviews) * 100 : 0;
        return (
            <div className="flex items-center gap-2 w-full">
                <span className="w-8 text-sm text-gray-600">{rating}</span>
                <div className="flex-1 h-3 bg-gray-200 rounded-full">
                    <div 
                        className="h-full bg-green-600 rounded-full" 
                        style={{ width: `${percentage}%` }}
                    />
                </div>
                <span className="w-12 text-sm text-gray-600">{count}</span>
            </div>
        );
    };

    const RatingStars = ({ rating }) => {
        const fullStars = Math.floor(rating);
        const hasHalfStar = rating % 1 >= 0.5;
        
        return (
            <div className="flex items-center">
                {[...Array(fullStars)].map((_, i) => (
                    <Star key={i} className="w-6 h-6 fill-green-600 text-green-600" />
                ))}
                {hasHalfStar && <StarHalf className="w-6 h-6 fill-green-600 text-green-600" />}
                {[...Array(5 - fullStars - (hasHalfStar ? 1 : 0))].map((_, i) => (
                    <Star key={`empty-${i}`} className="w-6 h-6 text-gray-300" />
                ))}
            </div>
        );
    };

    const totalReviews = comments.length;

    return (
        <ContainerHome>
            <div className="max-w-4xl mx-auto p-6">
                {tribunalData && (
                    <div className="mb-8">
                        <h2 className="text-2xl font-bold mb-4">{tribunalData.nome}</h2>
                        <img 
                            src={`http://localhost:3001/uploads/tribunais/${tribunalData.imagem}`} 
                            alt={tribunalData.nome} 
                            className="w-full h-48 object-cover rounded-lg mb-4"
                        />
                        <p className="text-gray-700">{tribunalData.descricao}</p>
                    </div>
                )}

                <div className="bg-white rounded-lg shadow p-6 mb-8">
                    <div className="flex flex-col md:flex-row gap-8 items-center">
                        <div className="text-center">
                            <div className="text-5xl font-bold text-gray-800 mb-2">
                                {averageRating.toFixed(1)}
                            </div>
                            <RatingStars rating={averageRating} />
                            <div className="text-sm text-gray-600 mt-2">
                                {totalReviews.toLocaleString()} avaliações
                            </div>
                        </div>

                        <div className="flex-1">
                            {[5, 4, 3, 2, 1].map(rating => (
                                <RatingBar 
                                    key={rating}
                                    rating={rating}
                                    count={ratingStats[rating]}
                                    totalReviews={totalReviews}
                                />
                            ))}
                        </div>
                    </div>
                </div>

                <Link 
                    to={`/login`}
                    className="inline-block bg-green-600 text-white px-6 py-2 rounded-full mb-8 hover:bg-green-700 transition-colors"
                >
                    Realizar seu Feedback
                </Link>

                <div className="space-y-4">
                    <h4 className="text-xl font-semibold">Comentários</h4>
                    {comments.length > 0 ? (
                        comments.map((comment, index) => (
                            <div 
                                key={index}
                                className="bg-white rounded-lg shadow p-4"
                            >
                                <div className="flex items-center gap-2 mb-2">
                                    <span className="font-semibold">Anônimo {comment.id_usuario}</span>
                                    <div className="flex items-center">
                                        <RatingStars rating={comment.avaliacao} />
                                    </div>
                                </div>
                                <p className="text-gray-700 mb-2">{comment.comentario}</p>
                                <p className="text-sm text-gray-500">
                                    {new Date(comment.data_criacao).toLocaleDateString()}
                                </p>
                            </div>
                        ))
                    ) : (
                        <p className="text-gray-600">Ainda sem comentários.</p>
                    )}
                </div>
            </div>
        </ContainerHome>
    );
};

export default FeedTribunais;
