import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from "./HeaderLggd.module.css";

function HeaderLggd({ children }) {
    const [isOpen, setIsOpen] = useState(false);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [showDetails, setShowDetails] = useState(false);
    const [showProgress, setShowProgress] = useState(false);
    const [showOverlay, setShowOverlay] = useState(false);
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

    const sidebarRef = useRef(null);
    const searchRef = useRef(null);
    const resultRefs = useRef([]);
    const navigate = useNavigate();

    const [user, setUser] = useState(null);
    const [firstLetter, setFirstLetter] = useState('');
    const [userColor, setUserColor] = useState('#000000');
    const [progress, setProgress] = useState(0);
    const [rewardToken, setRewardToken] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [activeIndex, setActiveIndex] = useState(-1);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isMobileSearchOptionsOpen, setIsMobileSearchOptionsOpen] = useState(false);
    

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 768);
        };

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const generateRandomToken = () => {
        const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        const length = 16;
        let token = '';
        for (let i = 0; i < length; i++) {
            token += characters.charAt(Math.floor(Math.random() * characters.length));
        }
        return token;
    };

    const fetchSearchResults = async (query) => {
        try {
            const endpoints = [
                { name: 'tribunais', idField: 'id_tribunal' },
                { name: 'foruns', idField: 'id_forum' },
                { name: 'juiz', idField: 'id_juiz' },
                { name: 'mediador', idField: 'id_mediador' },
                { name: 'advocacia', idField: 'id_advocacia' },
                { name: 'portais', idField: 'id_portal' }
            ];

            const promises = endpoints.map(({ name, idField }) =>
                axios.get(`http://localhost:3001/${name}`).then((res) =>
                    res.data.map((item) => ({
                        name: item.nome,
                        link: `/user/${name}/${item[idField]}/feedback`,
                    }))
                )
            );
            const results = await Promise.all(promises);
            const allItems = results.flat();
            const filteredItems = allItems.filter((item) =>
                item.name.toLowerCase().includes(query.toLowerCase())
            );
            setSearchResults(filteredItems);
        } catch (error) {
            console.error('Erro ao buscar dados:', error);
        }
    };

    const handleSearch = (e) => {
        e.preventDefault();
        if (searchQuery) fetchSearchResults(searchQuery);
    };

    const handleInputChange = (e) => {
        const query = e.target.value;
        setSearchQuery(query);
        setIsMenuOpen(!!query);
        if (query) {
            fetchSearchResults(query);
        } else {
            setSearchResults([]);
        }
    };

    const handleResultClick = (link) => {
        setIsMenuOpen(false);
        setSearchQuery('');
        navigate(link);
      };
    
      const handleKeyDown = (e) => {
        if (isMenuOpen) {
          if (e.key === 'ArrowDown') {
            setActiveIndex((prevIndex) => {
              const newIndex = prevIndex === searchResults.length - 1 ? 0 : prevIndex + 1;
              return newIndex;
            });
          } else if (e.key === 'ArrowUp') {
            setActiveIndex((prevIndex) => {
              const newIndex = prevIndex <= 0 ? searchResults.length - 1 : prevIndex - 1;
              return newIndex;
            });
          } else if (e.key === 'Enter' && activeIndex >= 0) {
            e.preventDefault();
            handleResultClick(searchResults[activeIndex].link);
          }
        }
      };

      const toggleMobileMenu = () => {
        setIsMobileMenuOpen(!isMobileMenuOpen);
        if (!isMobileMenuOpen) {
          setIsMobileSearchOptionsOpen(false);
        }
      };
    
      const toggleMobileSearchOptions = () => {
        setIsMobileSearchOptionsOpen(!isMobileSearchOptionsOpen);
      };
    
      const handleMobileMenuClick = () => {
        setIsMobileMenuOpen(false);
        setIsMobileSearchOptionsOpen(false);
      };

    const handleGenerateToken = () => {
        const token = generateRandomToken();
        setRewardToken(token);
        localStorage.setItem('rewardToken', token);
        setProgress(0);
        localStorage.setItem(`userProgress_${user.id}`, '0');
    };

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('usuarios'));
        if (storedUser) {
            setUser(storedUser);
            setFirstLetter(storedUser.nome.charAt(0).toUpperCase());

            let userColor = localStorage.getItem('userColor');
            if (!userColor) {
                userColor = generateRandomColor();
                localStorage.setItem('userColor', userColor);
            }
            setUserColor(userColor);

            const storedProgress = localStorage.getItem(`userProgress_${storedUser.id}`);
            setProgress(storedProgress ? parseInt(storedProgress) : 0);
        }
    }, []);

    const generateRandomColor = () => {
        const colors = [
            '#2196F3', '#4CAF50', '#9C27B0', '#F44336', '#FF9800',
            '#009688', '#3F51B5', '#795548', '#607D8B', '#E91E63'
        ];
        return colors[Math.floor(Math.random() * colors.length)];
    };

    useEffect(() => {
        if (activeIndex >= 0 && resultRefs.current[activeIndex]) {
          resultRefs.current[activeIndex].scrollIntoView({
            behavior: 'smooth',
            block: 'nearest',
          });
        }
      }, [activeIndex]);

      useEffect(() => {
        document.addEventListener('keydown', handleKeyDown);
        return () => document.removeEventListener('keydown', handleKeyDown);
      }, [isMenuOpen, activeIndex, searchResults]);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (sidebarRef.current && !sidebarRef.current.contains(event.target)) {
                if (isMobile) {
                    setIsOpen(false);
                    setShowOverlay(false);
                }
            }
            if (searchRef.current && !searchRef.current.contains(event.target)) {
                setIsMenuOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, [isMobile]);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('usuarios');
        localStorage.removeItem('userColor');
        window.location.href = '/';
    };

    const UserPanel = () => (
        <div className={isMobile ? styles.popupStyle : styles.sidebarContent}>
            <button className={styles.closeButton} onClick={() => { setIsOpen(false); setShowOverlay(false); }}>X</button>
            <div className={styles.userHeader}>
                <div className={styles.usuario} style={{ backgroundColor: userColor }}>{firstLetter}</div>
                <div className={styles.userName}>{user?.nome}</div>
            </div>
            {user?.role !== 'admin' && (
                <>
                    <button onClick={() => setShowDetails(!showDetails)} className={styles.menuItem}>
                        Dados do usuário
                    </button>
                    {showDetails && (
                        <div className={styles.userDetails}>
                            <div>CPF: {user?.cpf || 'Não disponível'}</div>
                            <div>Telefone: {user?.telefone || 'Não disponível'}</div>
                            <div>Email: {user?.email || 'Não disponível'}</div>
                        </div>
                    )}
                    <button onClick={() => setShowProgress(!showProgress)} className={styles.menuItem}>
                        Pontuação
                    </button>
                    {showProgress && (
                        <div className={styles.progressContainer}>
                            <div className={styles.progressBar}>
                                <div className={styles.progressFill} style={{ width: `${(progress / 1000) * 100}%` }} />
                            </div>
                            <div className={styles.progressText}>{progress} / 1000 pontos</div>
                            {progress >= 1000 && !rewardToken && (
                                <button onClick={handleGenerateToken} className={styles.rewardButton}>
                                    Gerar Token de Recompensa
                                </button>
                            )}
                            {rewardToken && (
                                <div className={styles.tokenContainer}>
                                    <p>Seu Token:</p>
                                    <p>{rewardToken}</p>
                                </div>
                            )}
                        </div>
                    )}
                </>
            )}
            <button onClick={handleLogout} className={styles.menuItem}>
                Logout
            </button>
        </div>
    );

    return (
        <header>
            <nav className={styles.navbar}>
                <Link to="/user/" className={styles.brandL}>
                    <span className={styles.brand}>JUSTIX</span>
                </Link>
        <div className={styles.searchContainer} ref={searchRef}>
          <form onSubmit={handleSearch} className={styles.searchForm}>
            <div className={styles.searchWrapper}>
              <input
                type="text"
                placeholder="Pesquisar..."
                value={searchQuery}
                onChange={handleInputChange}
                className={styles.searchInput}
              />
              <button type="submit" className={styles.searchButton}>
                Buscar
              </button>
            </div>
          </form>
          
          {isMenuOpen && searchResults.length > 0 && (
            <div className={styles.megaMenu}>
              <ul>
                {searchResults.map((result, index) => (
                  <li 
                    key={index} 
                    ref={(el) => (resultRefs.current[index] = el)}
                    className={`${styles.menuItem} ${activeIndex === index ? styles.activeItem : ''}`}
                  >
                    <Link 
                      to={result.link}
                      onClick={() => handleResultClick(result.link)}
                    >
                      {result.name}
                    </Link>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
                <div className={styles.navleft}>
                    <div className={styles.dropdown} onMouseEnter={() => setIsDropdownOpen(true)} onMouseLeave={() => setIsDropdownOpen(false)}>
                        <button className={styles.dropbtn}>Opções de busca</button>
                        {isDropdownOpen && (
                            <div className={styles.dropdownContent}>
                                <Link to="/user/tribunais">Tribunais</Link>
                                <Link to="/user/foruns">Fóruns</Link>
                                <Link to="/user/juiz">Juiz</Link>
                                <Link to="/user/mediador">Mediação</Link>
                                <Link to="/user/advocacia">Advocacia</Link>
                                <Link to="/user/portais">Portais</Link>
                            </div>
                        )}
                    </div>
                    <button className={styles.userButton} onClick={() => { setIsOpen(true); setShowOverlay(true); }}>
                        <span className={styles.usuario} style={{ backgroundColor: userColor }}>{firstLetter}</span>
                    </button>
                </div>
            </nav>
            {showOverlay && <div className={`${styles.overlay} ${showOverlay ? styles.show : ''}`} onClick={() => { setIsOpen(false); setShowOverlay(false); }} />}

<div ref={sidebarRef} className={`${styles.sidebar} ${isOpen ? styles.show : ''}`}>
    {isOpen && <UserPanel />}
</div>
        </header>
    );
}

export default HeaderLggd;
