// ForunsFormFeed.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import ContainerHome from '../../ContainerHome';

const ForunsFormFeed = ({ id_forum, fetchComments }) => {
    const navigate = useNavigate();
    const [newComment, setNewComment] = useState('');
    const [rating, setRating] = useState(1);
    const [protocolNumber, setProtocolNumber] = useState('');
    const [arrivalTime, setArrivalTime] = useState('');
    const [departureTime, setDepartureTime] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const handleSubmitComment = async (e) => {
        e.preventDefault();
        setError('');

        // Recupera os dados do usuário do localStorage
        const usuarioData = localStorage.getItem('usuarios');
        if (!usuarioData) {
            alert('Você precisa estar logado para comentar');
            navigate('/login');
            return;
        }

        const usuario = JSON.parse(usuarioData);
        
        try {
            setIsLoading(true);
            
            const commentData = {
                id_usuario: usuario.id, // Certifique-se de que isso está correto
                id_forum: parseInt(id_forum), // O id_forum deve ser um número
                numero_protocolo: protocolNumber, // Deve ter entre 5 e 20 caracteres
                comentario: newComment, // Pode ser uma string vazia
                avaliacao: parseInt(rating), // Deve ser um número entre 1 e 5
                horario_chegada: arrivalTime || null, // Pode ser null ou uma string de horário
                horario_saida: departureTime || null // Pode ser null ou uma string de horário
            };

            console.log('Enviando dados:', commentData);

            const response = await axios.post('http://localhost:3001/av_foruns/:', commentData);

            if (response.data.success) {
                // Limpa o formulário
                setNewComment('');
                setRating(1);
                setProtocolNumber('');
                setArrivalTime('');
                setDepartureTime('');
                
                // Atualiza os comentários
                await fetchComments();
                
                alert('Comentário adicionado com sucesso!');
            }
        } catch (error) {
            console.error('Erro ao adicionar comentário:', error);
            setError(error.response?.data?.error || 'Erro ao adicionar comentário');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <ContainerHome>
        <form onSubmit={handleSubmitComment} className="space-y-4 mb-8">
            {error && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                    {error}
                </div>
            )}
            <div>
                <label className="block mb-2">
                    Número de Protocolo:
                    <input
                        type="text"
                        value={protocolNumber}
                        onChange={(e) => setProtocolNumber(e.target.value)}
                        required
                        minLength="5"
                        maxLength="20"
                        className="w-full p-2 border rounded"
                    />
                </label>
            </div>

            <div>
                <label className="block mb-2">
                    Comentário:
                    <textarea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        required
                        className="w-full p-2 border rounded"
                        rows="4"
                    />
                </label>
            </div>

            <div>
                <label className="block mb-2">
                    Avaliação:
                    <select 
                        value={rating}
                        onChange={(e) => setRating(e.target.value)}
                        className="w-full p-2 border rounded"
                    >
                        {[1, 2, 3, 4, 5].map((star) => (
                            <option key={star} value={star}>
                                {'★'.repeat(star)} ({star})
                            </option>
                        ))}
                    </select>
                </label>
            </div>

            <div className="grid grid-cols-2 gap-4">
                <label className="block">
                    Horário de Chegada:
                    <input
                        type="time"
                        value={arrivalTime}
                        onChange={(e) => setArrivalTime(e.target.value)}
                        className="w-full p-2 border rounded"
                    />
                </label>

                <label className="block">
                    Horário de Saída:
                    <input
                        type="time"
                        value={departureTime}
                        onChange={(e) => setDepartureTime(e.target.value)}
                        className="w-full p-2 border rounded"
                    />
                </label>
            </div>

            <button 
                type="submit"
                disabled={isLoading}
                className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 disabled:bg-blue-300"
            >
                {isLoading ? 'Enviando...' : 'Enviar Comentário'}
            </button>
        </form>
        </ContainerHome>
    );
};

export default ForunsFormFeed;
