import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
import ContainerHome from '../../../components/ContainerHome/index';
import styles from '../../../pages/Login/Login.module.css';

const FeedTribunais = () => {
    const { id_tribunal } = useParams();
    const [comments, setComments] = useState([]);
    const [averageRating, setAverageRating] = useState(0);
    const [tribunalData, setTribunalData] = useState(null); // Estado para os dados do tribunal

    useEffect(() => {
        if (id_tribunal) {
            fetchComments();
            fetchTribunalData(); // Chama a nova função
        }
    }, [id_tribunal]);

    const fetchComments = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/av_tribunais/${id_tribunal}`);
            setComments(res.data);
        } catch (error) {
            console.error('Erro ao buscar comentários:', error);
        }
    };


    const fetchTribunalData = async () => {
        try {
            const res = await axios.get('http://localhost:3001/tribunais');
            
            // Buscar as avaliações atualizadas para cada tribunal
            const tribunaisWithRatings = await Promise.all(
              res.data.map(async (tribunal) => {
                try {
                  const ratingRes = await axios.get(`http://localhost:3001/tribunais_avaliacao/${tribunal.id_tribunal}`);
                  const mediaAvaliacao = parseFloat(ratingRes.data.media_avaliacao) || 0;
                  return {
                    ...tribunal,
                    avaliacao_media: mediaAvaliacao
                  };
                } catch (err) {
                  console.error(`Erro ao buscar avaliação para tribunal ${tribunal.id_tribunal}:`, err);
                  return {
                    ...tribunal,
                    avaliacao_media: 0
                  };
                }
              })
            );
      
            setTribunailData(tribunaisWithRatings);
            
          } catch (err) {
            console.error('Erro ao listar tribunais:', err);
          }
        };

    return (
        <ContainerHome>
            <div>
                {id_tribunal && (
                    <Link to={`/login`} className={styles.feedbtn}>
                        Realizar seu Feedback?
                    </Link>
                )}
                {tribunalData && ( // Renderiza os dados do tribunal
                    <div className={styles.tribunalInfo}>
                        <h2>{tribunalData.nome}</h2>
                        <img 
                            src={`http://localhost:3001/uploads/tribunais/${tribunalData.imagem}`} 
                            alt={tribunalData.nome} 
                            style={{ maxWidth: '100%', height: 'auto' }} // Estilos para ajustar a imagem
                        />
                        <p>{tribunalData.descricao}</p> {/* Supondo que 'descricao' seja um campo da entidade */}
                    </div>
                )}
                <div className={styles.feedtxt}>
                    <h3>Média: ★ {averageRating}</h3>
                    <h4>Comentários</h4>
                </div>
                {comments.length > 0 ? (
                    comments.map((comment, index) => (
                        <section className={styles.CSection} key={index}>
                            <div className={styles.commentContainer}>
                                <p>
                                    <strong>{comment.nome}</strong> Anonimous {comment.id_usuario} - 
                                    ★ {comment.avaliacao}
                                </p>
                                <div className={styles.commentarea}>
                                    <p>{comment.comentario}</p>
                                </div>
                                <p>Data: {new Date(comment.data_criacao).toLocaleDateString()}</p>
                            </div>
                        </section>
                    ))
                ) : (
                    <p>Ainda sem comentários.</p>
                )}
            </div>
        </ContainerHome>
    );
};

export default FeedTribunais;
