import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import style from '../Card/Card.module.css';

const PortaisListPageO = () => {
  const [portais, setPortais] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    listarPortais();
  }, []);

  const listarPortais = async () => {
    try {
      const res = await axios.get('http://localhost:3001/portais');
      setPortais(res.data);
    } catch (err) {
      console.error('Erro ao listar portais:', err);
    }
  };

  const excluirPortais = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/portais/${id}`);
      listarPortais();
    } catch (err) {
      console.error('Erro ao excluir portais:', err);
    }
  };

  const handleEdit = (portais) => {
    navigate('/admin/dashboard/portais/edit', { state: { portais } });
  };

  const getImagemUrl = (imagem) => {
    if (!imagem) return null;
    // Verifique se a imagem já contém o prefixo
    if (imagem.startsWith('/uploads/portais/')) {
      return `http://localhost:3001${imagem}`; // Apenas retorna a URL completa
    }
    return `http://localhost:3001/uploads/portais/${imagem}`; // Adiciona o prefixo se não estiver presente
  };

  return (
    <div> 
          {portais.map(portais => (
            <div key={portais.id_portais} className={style.card}>
              <div className={style.cardleft}>
                {portais.imagem ? (
                  <img
                    src={getImagemUrl(portais.imagem)}
                    alt={`Imagem de ${portais.nome}`}
                    className={style.cardleft1}
                    
                  />
                ) : (
                  <div className={style.profileimg}>
                    Sem imagem
                  </div>
                )}
               <div className={style.cardinfo}>
              <h3>{portais.nome}</h3>
              <p className={style.tag}>
                {portais.url} 
              </p>
              <p className={style.tag1}>
                Média:  ★ {portais.avaliacao_media}
              </p>
            </div>
          </div>
              <div>
                <button
                  className={style.visualizarbtn}
                  onClick={() => handleEdit(portais)}
                  style={{ marginRight: '5px' }}
                >
                  Editar
                </button>
                <button 
                  className={style.visualizarbtn}
                  onClick={() => excluirPortais(portais.id_portais)}
                >
                  Excluir
                </button>
              </div>
            </div>
          ))}
    </div>
  );
};

export default PortaisListPageO;
