import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Search, MapPin, Star, Clock, MessageCircle, ThumbsUp } from 'lucide-react';

const TribunaisListPageUser = () => {
  const [tribunais, setTribunais] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('avaliacao');

  useEffect(() => {
    listarTribunais();
  }, []);

  const listarTribunais = async () => {
    try {
      const res = await axios.get('http://localhost:3001/tribunais');
      const tribunaisWithRatings = await Promise.all(
        res.data.map(async (tribunal) => {
          try {
            const ratingRes = await axios.get(`http://localhost:3001/tribunais_avaliacao/${tribunal.id_tribunal}`);
            return {
              ...tribunal,
              avaliacao_media: parseFloat(ratingRes.data.media_avaliacao) || 0,
              total_avaliacoes: Math.floor(Math.random() * 1000), // Simulated data
              resolvidos: Math.floor(Math.random() * 100), // Simulated data
              tempo_medio_resposta: Math.floor(Math.random() * 24) + 1 // Simulated data
            };
          } catch (err) {
            console.error(`Erro ao buscar avaliação para tribunal ${tribunal.id_tribunal}:`, err);
            return {
              ...tribunal,
              avaliacao_media: 0,
              total_avaliacoes: 0,
              resolvidos: 0,
              tempo_medio_resposta: 0
            };
          }
        })
      );
      setTribunais(tribunaisWithRatings);
    } catch (err) {
      console.error('Erro ao listar tribunais:', err);
    }
  };

  const getImagemUrl = (imagem) => {
    if (!imagem) return null;
    if (imagem.startsWith('/uploads/tribunais/')) {
      return `http://localhost:3001${imagem}`;
    }
    return `http://localhost:3001/uploads/tribunais/${imagem}`;
  };

  const handleVisualizarClick = (id_tribunal) => {
    localStorage.setItem('id_tribunal', id_tribunal);
  };

  const filteredTribunais = tribunais.filter(tribunal => 
    tribunal.nome.toLowerCase().includes(searchTerm.toLowerCase()) || 
    tribunal.endereco.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const formatRating = (rating) => {
    const numRating = parseFloat(rating);
    return isNaN(numRating) ? "0.0" : numRating.toFixed(1);
  };

  const getRatingColor = (rating) => {
    if (rating >= 7) return 'text-green-600';
    if (rating >= 5) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getReputationLabel = (rating) => {
    if (rating >= 7) return 'Ótimo';
    if (rating >= 5) return 'Regular';
    return 'Ruim';
  };

  return (
    <div className="max-w-6xl mx-auto p-4">
      <div className="mb-8 bg-white rounded-lg shadow-md p-4">
        <div className="relative">
          <input
            type="text"
            placeholder="Buscar tribunal..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full p-4 pl-12 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
          <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
        </div>
        
        <div className="flex gap-4 mt-4">
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="p-2 border border-gray-300 rounded-md"
          >
            <option value="avaliacao">Ordenar por Avaliação</option>
            <option value="recentes">Mais Recentes</option>
            <option value="nome">Nome</option>
          </select>
        </div>
      </div>

      <div className="space-y-4">
        {filteredTribunais.map(tribunal => (
          <div key={tribunal.id_tribunal} className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow">
            <div className="flex items-start gap-6">
              <div className="w-24 h-24 flex-shrink-0">
                {tribunal.imagem ? (
                  <img
                    src={getImagemUrl(tribunal.imagem)}
                    alt={`Logo de ${tribunal.nome}`}
                    className="w-full h-full object-cover rounded-lg"
                  />
                ) : (
                  <div className="w-full h-full bg-gray-200 rounded-lg flex items-center justify-center text-gray-500 text-sm">
                    Sem imagem
                  </div>
                )}
              </div>

              <div className="flex-grow">
                <div className="flex justify-between items-start">
                  <h2 className="text-xl font-semibold text-gray-900">{tribunal.nome}</h2>
                  <div className={`text-center ${getRatingColor(tribunal.avaliacao_media)}`}>
                    <div className="text-2xl font-bold">{formatRating(tribunal.avaliacao_media)}</div>
                    <div className="text-sm font-medium">{getReputationLabel(tribunal.avaliacao_media)}</div>
                  </div>
                </div>

                <div className="flex items-center gap-2 text-gray-600 mt-2">
                  <MapPin size={16} />
                  <span className="text-sm">
                    {tribunal.endereco}, {tribunal.cidade} - {tribunal.estado}
                  </span>
                </div>

                <div className="grid grid-cols-3 gap-4 mt-4">
                  <div className="flex items-center gap-2 text-gray-600">
                    <MessageCircle size={16} />
                    <span className="text-sm">{tribunal.total_avaliacoes} avaliações</span>
                  </div>
                  <div className="flex items-center gap-2 text-gray-600">
                    <ThumbsUp size={16} />
                    <span className="text-sm">{tribunal.resolvidos}% resolvidos</span>
                  </div>
                  <div className="flex items-center gap-2 text-gray-600">
                    <Clock size={16} />
                    <span className="text-sm">{tribunal.tempo_medio_resposta}h média resposta</span>
                  </div>
                </div>
              </div>

              <Link
                to={`/user/dashboard/tribunais/${tribunal.id_tribunal}/feedback`}
                onClick={() => handleVisualizarClick(tribunal.id_tribunal)}
                className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                Ver Avaliações
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TribunaisListPageUser;
