import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ChevronLeft, ChevronRight } from 'lucide-react';

const HomeCards = () => {
  const [tribunais, setTribunais] = useState([]);
  const [foruns, setForuns] = useState([]);
  const [juizes, setJuizes] = useState([]);
  const [mediadores, setMediadores] = useState([]);
  const [advocacia, setAdvocacia] = useState([]);
  const [portais, setPortais] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        await Promise.all([
          listarEntidades('tribunais', setTribunais, 'tribunais_avaliacao'),
          listarEntidades('foruns', setForuns),
          listarEntidades('juiz', setJuizes, 'juiz_avaliacao'),
          listarEntidades('mediador', setMediadores),
          listarEntidades('advocacia', setAdvocacia),
          listarEntidades('portais', setPortais),
        ]);
      } catch (error) {
        console.error('Erro ao carregar dados:', error);
      }
    };

    fetchData();
  }, []);

  const listarEntidades = async (endpoint, setData, ratingEndpoint) => {
    try {
      const res = await axios.get(`http://localhost:3001/${endpoint}`);
      const entidadesWithRatings = await Promise.all(
        res.data.map(async (entidade) => {
          if (ratingEndpoint) {
            try {
              const ratingRes = await axios.get(`http://localhost:3001/${ratingEndpoint}/${entidade.id}`);
              const mediaAvaliacao = parseFloat(ratingRes.data.media_avaliacao) || 0;
              return {
                ...entidade,
                avaliacao_media: mediaAvaliacao
              };
            } catch (err) {
              console.error(`Erro ao buscar avaliaÃ§Ã£o para ${endpoint} ${entidade.id}:`, err);
              return {
                ...entidade,
                avaliacao_media: 0
              };
            }
          }
          return entidade;
        })
      );
      setData(entidadesWithRatings);
    } catch (err) {
      console.error(`Erro ao listar ${endpoint}:`, err);
    }
  };

  const getImageUrl = (type, imagem) => {
    if (!imagem) return null;
    if (imagem.startsWith('/uploads/${type}/')) {
      return `http://localhost:3001${imagem}`;
    }
    return `http://localhost:3001/uploads/${type}/${imagem}`;
  };

  const getRatingEmoji = (rating) => {
    const numRating = parseFloat(rating);
    if (numRating >= 8) return 'ðŸ˜Š';
    if (numRating >= 6) return 'ðŸ˜';
    return 'ðŸ˜ž';
  };

  const getRatingColor = (rating) => {
    const numRating = parseFloat(rating);
    if (numRating >= 8) return 'text-green-600';
    if (numRating >= 6) return 'text-blue-600';
    return 'text-red-600';
  };

  const formatRating = (rating) => {
    const numRating = parseFloat(rating);
    return isNaN(numRating) ? "0.0" : numRating.toFixed(1);
  };

  const Card = ({ item, type }) => {
    const rating = formatRating(item.avaliacao_media);
    
    return (
      <div className="bg-white rounded-lg shadow p-4 w-64 hover:shadow-lg transition-shadow">
        <div className="flex flex-col items-center space-y-4">
          <div className="w-16 h-16 rounded-full bg-gray-100 flex items-center justify-center overflow-hidden">
            {item.imagem ? (
              <img 
                src={getImageUrl(type, item.imagem)} 
                alt={item.nome} 
                className="w-full h-full object-cover"
              />
            ) : (
              <div className="text-3xl text-gray-400">
                {item.nome?.charAt(0).toLowerCase() || '?'}
              </div>
            )}
          </div>
          <div className="text-center font-medium">{item.nome}</div>
          <div className="text-sm text-gray-600 text-center">
            Resolve {(parseFloat(rating) * 10).toFixed(1)}% dos problemas
          </div>
          <div className="flex items-center space-x-2">
            <span className="text-2xl">
              {getRatingEmoji(rating)}
            </span>
            <span className={`text-xl font-bold ${getRatingColor(rating)}`}>
              {rating}
            </span>
            <span className="text-gray-400 text-sm">/10</span>
          </div>
        </div>
      </div>
    );
  };

  const CardCarousel = ({ title, items, type }) => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const visibleCards = 4;

    const nextSlide = () => {
      setCurrentIndex((prevIndex) => 
        prevIndex + 1 >= items.length ? 0 : prevIndex + 1
      );
    };

    const prevSlide = () => {
      setCurrentIndex((prevIndex) => 
        prevIndex - 1 < 0 ? items.length - 1 : prevIndex - 1
      );
    };

    return (
      <div className="mb-8">
        <h2 className="text-gray-600 text-xl mb-6">{title}</h2>
        
        <div className="relative">
          <button 
            onClick={prevSlide}
            className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-4 z-10 bg-white rounded-full w-8 h-8 shadow-lg hover:bg-gray-50 flex items-center justify-center"
          >
            <ChevronLeft size={24} />
          </button>

          <div className="flex space-x-4 overflow-hidden px-4">
            {items.map((item, idx) => {
              const position = (idx - currentIndex + items.length) % items.length;
              if (position < visibleCards) {
                return <Card key={item.id || idx} item={item} type={type} />;
              }
              return null;
            })}
          </div>

          <button 
            onClick={nextSlide}
            className="absolute right-0 top-1/2 -translate-y-1/2 translate-x-4 z-10 bg-white rounded-full w-8 h-8 shadow-lg hover:bg-gray-50 flex items-center justify-center"
          >
            <ChevronRight size={24} />
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <CardCarousel title="Tribunais em Destaque" items={tribunais} type="tribunais" />
      <CardCarousel title="FÃ³runs em Destaque" items={foruns} type="foruns" />
      <CardCarousel title="JuÃ­zes em Destaque" items={juizes} type="juiz" />
      <CardCarousel title="Mediadores em Destaque" items={mediadores} type="mediador" />
      <CardCarousel title="Advocacias em Destaque" items={advocacia} type="advocacia" />
      <CardCarousel title="Portais em Destaque" items={portais} type="portais" />
    </div>
  );
};

export default HomeCards;
