import React, { useEffect, useState } from 'react';
import { Scale, Star, Gavel, BookOpen, FileText, Award, Users } from 'lucide-react';
import '../Home/Banner.css';

const DynamicBannerUser = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isVisible, setIsVisible] = useState(false);
  const [firstName, setFirstName] = useState('');

  useEffect(() => {
    // Configuração da visibilidade inicial
    setIsVisible(true);

    // Atualização da posição do mouse
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX - window.innerWidth / 2) / 50,
        y: (e.clientY - window.innerHeight / 2) / 50,
      });
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Obtenção do primeiro nome do usuário
    const storedUser = JSON.parse(localStorage.getItem('usuarios'));
    if (storedUser && storedUser.nome) {
      const firstName = storedUser.nome.split(' ')[0]; // Extrai o primeiro nome
      setFirstName(firstName);
    }

    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const features = [
    { text: "Avalie", icon: Star, delay: 0 },
    { text: "Contribua", icon: FileText, delay: 0.2 },
    { text: "Ganhe Benefícios", icon: Award, delay: 0.4 }
  ];

  return (
    <div className={`banner-container ${isVisible ? 'visible' : ''}`} style={{
      transform: `translate(${mousePosition.x}px, ${mousePosition.y}px)`
    }}>
      <h1>Bem vindo ao Justix, {firstName}!</h1>
      <p>
        Aqui você não apenas compartilha sua opinião sobre ações jurídicas, você também é recompensado por isso!
        Avalie, contribua com suas análises e adquira isenção de custo no seu próximo processo ao alcançar metas de avaliações.
      </p>

      <div className="features">
        {features.map((feature, index) => (
          <div key={index} className="feature" style={{ animationDelay: `${feature.delay}s` }}>
            <feature.icon />
            <span>{feature.text}</span>
          </div>
        ))}
      </div>

      <div className="icons">
        {[Scale, Gavel, BookOpen, Users].map((Icon, index) => (
          <Icon key={index} className="icon" />
        ))}
      </div>

      <div className="background-pattern">
        {[...Array(8)].map((_, i) => (
          <div key={i} className="pattern-element" />
        ))}
      </div>
    </div>
  );
};

export default DynamicBannerUser;
