// DynamicBanner.js
import React, { useEffect, useState } from "react";
import styles from "./";

const DynamicBanner = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      setIsVisible(scrollY > 100); // Exibir após o usuário rolar 100px
    };

    const handleMouseMove = (event) => {
      setMousePosition({ x: event.clientX, y: event.clientY });
    };

    window.addEventListener("scroll", handleScroll);
    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("scroll", handleScroll);
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <div
      className={`${styles.banner} ${isVisible ? styles.visible : ""}`}
      style={{
        backgroundPositionX: `${mousePosition.x / 50}px`,
        backgroundPositionY: `${mousePosition.y / 50}px`,
      }}
    >
      <h1 className={styles.title}>Bem-vindo ao Dynamic Banner</h1>
      <p className={styles.subtitle}>Descubra conteúdos incríveis</p>
    </div>
  );
};

export default DynamicBanner;
