import React, { useState, useEffect, useRef } from 'react';
import styles from "./HeaderLggd.module.css";
import axios from 'axios';
import { Link } from 'react-router-dom';

function HeaderLggd({ children }) {
    const [isOpen, setIsOpen] = useState(false);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [showDetails, setShowDetails] = useState(false);
    const [showProgress, setShowProgress] = useState(false);
    const popupRef = useRef(null);
    const searchRef = useRef(null);
    const [user, setUser] = useState(null);
    const [firstLetter, setFirstLetter] = useState('');
    const [userColor, setUserColor] = useState('#000000');
    const [progress, setProgress] = useState(0);
    const [rewardToken, setRewardToken] = useState('');

    // Search related states
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const generateRandomToken = () => {
        const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        const length = 16;
        let token = '';
        for (let i = 0; i < length; i++) {
            token += characters.charAt(Math.floor(Math.random() * characters.length));
        }
        return token;
    };

    const fetchSearchResults = async (query) => {
        try {
            const endpoints = [
                { name: 'tribunais', idField: 'id_tribunal' },
                { name: 'foruns', idField: 'id_forum' },
                { name: 'juiz', idField: 'id_juiz' },
                { name: 'mediador', idField: 'id_mediador' },
                { name: 'advocacia', idField: 'id_advocacia' },
                { name: 'portais', idField: 'id_portal' }
            ];

            const promises = endpoints.map(({ name, idField }) =>
                axios.get(`http://localhost:3001/${name}`).then((res) =>
                    res.data.map((item) => ({
                        name: item.nome,
                        link: `/user/${name}/${item[idField]}/feedback`,
                    }))
                )
            );
            const results = await Promise.all(promises);
            const allItems = results.flat();
            const filteredItems = allItems.filter((item) =>
                item.name.toLowerCase().includes(query.toLowerCase())
            );
            setSearchResults(filteredItems);
        } catch (error) {
            console.error('Erro ao buscar dados:', error);
        }
    };

    const handleSearch = (e) => {
        e.preventDefault();
        if (searchQuery) fetchSearchResults(searchQuery);
    };

    const handleInputChange = (e) => {
        const query = e.target.value;
        setSearchQuery(query);
        setIsMenuOpen(!!query);
        if (query) {
            fetchSearchResults(query);
        } else {
            setSearchResults([]);
        }
    };

    const handleGenerateToken = () => {
        const token = generateRandomToken();
        setRewardToken(token);
        localStorage.setItem('rewardToken', token);
    };

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('usuarios'));
        if (storedUser) {
            setUser(storedUser);
            setFirstLetter(storedUser.nome.charAt(0).toUpperCase());
            
            let userColor = localStorage.getItem('userColor');
            if (!userColor) {
                userColor = generateRandomColor();
                localStorage.setItem('userColor', userColor);
            }
            setUserColor(userColor);

            const storedProgress = localStorage.getItem(`userProgress_${storedUser.id}`);
            setProgress(storedProgress ? parseInt(storedProgress) : 0);
        }
    }, []);

    const generateRandomColor = () => {
        const colors = [
            '#2196F3', '#4CAF50', '#9C27B0', '#F44336', '#FF9800',
            '#009688', '#3F51B5', '#795548', '#607D8B', '#E91E63'
        ];
        return colors[Math.floor(Math.random() * colors.length)];
    };

    useEffect(() => {
        const handleClickOutside = (event) => {
            // Verifica se o clique foi fora do popup do usuário
            if (popupRef.current && !popupRef.current.contains(event.target)) {
                setIsOpen(false);
                setShowDetails(false);
                setShowProgress(false);
            }
            
            // Verifica se o clique foi fora da área de pesquisa
            if (searchRef.current && !searchRef.current.contains(event.target)) {
                setIsMenuOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('usuarios');
        localStorage.removeItem('userColor');
        window.location.href = '/';
    };

    const handleResultClick = (link) => {
        setIsMenuOpen(false);
        setSearchQuery('');
    };

    return (
        <header>
            <nav className={styles.navbar}>
                <Link to="/user/" className={styles.brandL}>
                    <span className={styles.brand}>JUSTIX</span>
                </Link>

                {/* Search Container */}
                <div className={styles.searchContainer} ref={searchRef}>
                    <form onSubmit={handleSearch} className={styles.searchForm}>
                        <div className={styles.searchWrapper}>
                            <input
                                type="text"
                                placeholder="Pesquisar..."
                                value={searchQuery}
                                onChange={handleInputChange}
                                className={styles.searchInput}
                            />
                            <button type="submit" className={styles.searchButton}>
                                Buscar
                            </button>
                        </div>
                    </form>
                    {/* Mega Menu */}
                    {isMenuOpen && searchResults.length > 0 && (
                        <div className={styles.megaMenu}>
                            <ul>
                                {searchResults.map((result, index) => (
                                    <li key={index}>
                                        <Link 
                                            to={result.link}
                                            onClick={() => handleResultClick(result.link)}
                                        >
                                            {result.name}
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>
                <div 
            className={styles.dropdown}
            onMouseEnter={() => setIsDropdownOpen(true)}
            onMouseLeave={() => setIsDropdownOpen(false)}
          >
            <div className={styles.opt}></div>
            <button className={styles.navlinks}>Opções de busca</button>
            {isDropdownOpen && (
              <div className={styles.dropdownContent}>
                <Link to="/user/tribunais">Tribunais</Link>
                <Link to="/user/foruns">Fóruns</Link>
                <Link to="/user/juiz">Audiencias</Link>
                <Link to="/user/mediacoes">Mediações</Link>
                <Link to="/user/advocacia">Advocacia</Link>
                <Link to="/user/portais">Portais</Link>
              </div>
            )}
          </div>
                <div className={styles.navleft} ref={popupRef}>
                    {isOpen && (
                        <div className={styles.popupStyle}>
                            <div className={styles.usuario} style={{ backgroundColor: userColor }}>{firstLetter}</div>
                            <div className={styles.popupLinkStyle}>{user?.nome}</div>

                            {user?.role !== 'admin' ? (
                                <>
                                    <button
                                        onClick={() => setShowDetails(!showDetails)}
                                        className={styles.popupLinkStyle}
                                    >
                                        Dados do usuário
                                    </button>

                                    {showDetails && (
                                        <div style={{ borderRadius: '10px', backgroundColor: 'white', paddingLeft: '16px', paddingRight: '16px', color: '#fffff', fontSize: '13px' }}>
                                            <div>CPF: {user?.cpf || 'Não disponível'}</div>
                                            <div>Telefone: {user?.telefone || 'Não disponível'}</div>
                                            <div>Email: {user?.email || 'Não disponível'}</div>
                                        </div>
                                    )}

                                    <button 
                                        onClick={() => setShowProgress(!showProgress)}
                                        className={styles.popupLinkStyle}
                                    >
                                        Pontuação
                                    </button>

                                    {showProgress && (
                                        <div className="p-4 bg-white rounded-lg shadow-md">
                                            <div className="w-full h-6 bg-black rounded-full overflow-hidden">
                                                <div 
                                                    className="h-full bg-blue-500 transition-all duration-300"
                                                    style={{ width: `${(progress / 1000) * 100}%` }}
                                                ></div>
                                            </div>
                                            <div className="mt-2 text-center text-sm">
                                                {progress} / 1000 pontos
                                            </div>
                                            {progress >= 1000 && !rewardToken && (
                                                <button
                                                    onClick={handleGenerateToken}
                                                    className="mt-2 w-full py-2 bg-green-500 text-white rounded hover:bg-green-600 transition-colors"
                                                >
                                                    Gerar Token de Recompensa
                                                </button>
                                            )}
                                            {rewardToken && (
                                                <div className="mt-2 p-2 bg-gray-100 rounded text-center break-all">
                                                    <p className="text-xs font-medium">Seu Token:</p>
                                                    <p className="text-sm">{rewardToken}</p>
                                                </div>
                                            )}
                                        </div>
                                    )}
                                </>
                            ) : null}

                            <button
                                onClick={handleLogout}
                                className={styles.popupLinkStyle}
                            >
                                Logout
                            </button>
                        </div>
                    )}
                </div>
                {children}
                <button
                    className={styles.usuario}
                    onClick={() => setIsOpen(!isOpen)}
                    style={{ backgroundColor: userColor }}
                >
                    {firstLetter}
                </button>
            </nav>
        </header>
    );
}

export default HeaderLggd;
