import React, { useState } from 'react';
import { Link } from "react-router-dom";
import styles from "./Login.module.css";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import ContainerHome from "../../components/ContainerHome";
import { useAuth } from '../../contexts/AuthContext';

function Login() {
    const { login } = useAuth();
    const navigate = useNavigate();
  
    const handleSubmit = async (e) => {
      e.preventDefault();
  
      try {
        const response = await fetch('http://localhost:3001/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            email: formData.email,
            senha: formData.senha
          })
        });
  
        const data = await response.json();
  
        if (response.ok) {
          login(data);
          
          // Redirecionar baseado no role
          switch(data.user.role) {
            case 'admin':
              navigate('/admin/dashboard');
              break;
            case 'juiz':
              navigate('/juiz/dashboard');
              break;
            default:
              navigate('/user/dashboard');
          }
        } else {
          setError(data.error);
        }
      } catch (error) {
        setError('Erro ao fazer login');
      }
    };
  

    return (
        <>
            <Header>
                <div className={styles.navleft}>
                    <Link to="/Cadastro" className={styles.cadastrese}>Cadastre-se</Link>
                    <Link to="/Sobre_nos" className={styles.navlinks}>Sobre nós</Link>
                </div>
            </Header>
            <ContainerHome>
                <section className={styles.loginSection}>
                    <div className={styles.loginContainer}>
                        <Link to="/" className={styles.backButton}>
                            <span className={styles.backArrow}>←</span>
                            Voltar
                        </Link>
                        
                        <h2 className={styles.title}>LOGIN</h2>
                        
                        <form onSubmit={handleSubmit} className={styles.loginForm}>
                            <div className={styles.formGroup}>
                                <label htmlFor="email">E-mail:</label>
                                <input
                                    type="email"
                                    id="email"
                                    value={formData.email}
                                    onChange={handleInputChange}
                                    className={errors.email ? styles.inputError : ''}
                                    required
                                />
                                {errors.email && (
                                    <span className={styles.errorText}>{errors.email}</span>
                                )}
                            </div>

                            <div className={styles.formGroup}>
                                <label htmlFor="senha">Senha:</label>
                                <input
                                    type="password"
                                    id="senha"
                                    value={formData.senha}
                                    onChange={handleInputChange}
                                    className={errors.senha ? styles.inputError : ''}
                                    required
                                />
                                {errors.senha && (
                                    <span className={styles.errorText}>{errors.senha}</span>
                                )}
                            </div>
                            <Link to="/Tribunais">
                            <button type="submit" className={styles.submitButton}>
                                Entrar
                            </button>
                            </Link>
                        </form>
                    </div>
                </section>
            </ContainerHome>
            <Footer />
        </>
    );
}

export default Login;
