import React, { useState } from 'react';
import { Link, useLocation } from "react-router-dom";
import styles from "./HeaderLggd.module.css";

const getRandomColor = () => {
  const colors = [
    'bg-blue-600',
    'bg-purple-600',
    'bg-green-600',
    'bg-red-600',
    'bg-indigo-600',
    'bg-pink-600',
    'bg-teal-600'
  ];
  return colors[Math.floor(Math.random() * colors.length)];
};

function HeaderLggd({children}) {
    const location = useLocation();
    const [isOpen, setIsOpen] = useState(false);
    const [avatarColor] = useState(getRandomColor());
    
    // Get user data from localStorage
    const userData = JSON.parse(localStorage.getItem('user')) || {};
    const firstLetter = userData.nome ? userData.nome.charAt(0).toUpperCase() : 'U';


    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        window.location.href = '/login';
    };

    return (
        <header>
            <nav className={styles.navbar}>
                <div className={styles.navleft}>
                    <div className="relative">
                        <button
                            onClick={() => setIsOpen(!isOpen)}
                            className={`w-10 h-10 rounded-full ${avatarColor} text-white flex items-center justify-center font-semibold focus:outline-none text-lg`}
                        >
                            {firstLetter}
                        </button>

                        {isOpen && (
                            <div className="absolute left-0 mt-2 w-48 rounded-lg shadow-lg bg-gray-700 text-white z-50">
                                <div className="py-2">
                                    <Link 
                                        to="/perfil" 
                                        className="block px-4 py-2 hover:bg-gray-600 transition-colors"
                                    >
                                        Dados do usuario
                                    </Link>
                                    <Link 
                                        to="/pontuacao" 
                                        className="block px-4 py-2 hover:bg-gray-600 transition-colors"
                                    >
                                        Pontuação
                                    </Link>
                                    <button
                                        onClick={handleLogout}
                                        className="block w-full text-left px-4 py-2 hover:bg-gray-600 transition-colors"
                                    >
                                        Logout
                                    </button>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
                {children}
                <span className={styles.brand}>JUSTIX</span>
            </nav>
        </header>
    );
}

export default HeaderLggd;
