import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import styles from '../../pages/Login/Login.module.css';
import ContainerHome from '../ContainerHome';

const PortaisFormPageO = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const portaisAtivo = location.state?.portais || null;

  const [formData, setFormData] = useState({
    nome: '',
    url: '',
    avaliacao_media: 0
  });

  const [imagemFile, setImagemFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);


  useEffect(() => {
    if (portaisAtivo) {
      setFormData(portaisAtivo);
      if (portaisAtivo.imagem) {
        setPreviewUrl(`http://localhost:3001/uploads/${portaisAtivo.imagem}`);
      }
    }
  }, [portaisAtivo]);

  const handleImagemChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImagemFile(file);
      const fileUrl = URL.createObjectURL(file);
      setPreviewUrl(fileUrl);
    }
  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const submitFormData = new FormData();
      
      // Adiciona todos os campos do formulário ao FormData
      Object.keys(formData).forEach(key => {
        submitFormData.append(key, formData[key]);
      });

      // Adiciona a imagem apenas se uma nova imagem foi selecionada
      if (imagemFile) {
        submitFormData.append('imagem', imagemFile);
      }

      if (portaisAtivo) {
        await axios.put(
          `http://localhost:3001/portais/${portaisAtivo.id_portais}`,
          submitFormData,
          {
            headers: {
              'Content-Type': 'multipart/form-data'
            }
          }
        );
      } else {
        await axios.post(
          'http://localhost:3001/portais',
          submitFormData,
          {
            headers: {
              'Content-Type': 'multipart/form-data'
            }
          }
        );
      }
      navigate('/admin/dashboard/portais');
    } catch (err) {
      console.error('Erro ao salvar portais:', err);
    }
  };

  const handleCancel = () => {
    navigate('/admin/dashboard/portais');
  };

  return (
    <ContainerHome>
      <section className={styles.loginSection}>
    <div className={styles.loginContainer}>
      <h3 className={styles.title}>{portaisAtivo ? 'Editar Portal' : 'Novo Portal'}</h3>
      <form onSubmit={handleSubmit} className={styles.loginForm}>
        <div className={styles.formGroup}>
          <label>Nome:</label>
          <input
            type="text"
            value={formData.nome}
            onChange={(e) => setFormData({ ...formData, nome: e.target.value })}
            required
          />
        </div>
        <div className={styles.formGroup}>
          <label>URL:</label>
          <input
            type="text"
            value={formData.url}
            onChange={(e) => setFormData({ ...formData, url: e.target.value })}
            required
          />
        </div>

        <div className={styles.formGroup}>
          <label>Avaliação Média:</label>
          <input
            type="number"
            value={formData.avaliacao_media}
            onChange={(e) => setFormData({ ...formData, avaliacao_media: e.target.value })}
            step="0.01"
            min="0"
            max="10"
            required
          />
        </div>
        <div className={styles.formGroup}>
          <label>Foto:</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleImagemChange}
          />
          {previewUrl && (
            <div>
              <img 
                src={previewUrl} 
                alt="Preview" 
                style={{ maxWidth: '200px', marginTop: '10px' }}
              />
            </div>
          )}
        </div>
        <button type="submit" className={styles.submitButton}>
          {portaisAtivo ? 'Salvar Alterações' : 'Criar Portais'}
        </button>
        <button type="button" onClick={handleCancel} className={styles.submitButton}>
          Cancelar
        </button>
      </form>
    </div>
    </section>
    </ContainerHome>
  );
};

export default PortaisFormPageO;
