import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './TribunaisListPageUser.css';

const TribunaisListPageUser = () => {
  const [tribunais, setTribunais] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    listarTribunais();
  }, []);

  const listarTribunais = async () => {
    try {
      const res = await axios.get('http://localhost:3001/tribunais');
      const tribunaisWithRatings = await Promise.all(
        res.data.map(async (tribunal) => {
          try {
            const ratingRes = await axios.get(`http://localhost:3001/tribunais_avaliacao/${tribunal.id_tribunal}`);
            return {
              ...tribunal,
              avaliacao_media: parseFloat(ratingRes.data.media_avaliacao) || 0,
              total_avaliacoes: Math.floor(Math.random() * 1000), // Simulado
              tempo_resposta: Math.floor(Math.random() * 48), // Simulado
              resolvidos: Math.floor(Math.random() * 100), // Simulado
            };
          } catch (err) {
            return {
              ...tribunal,
              avaliacao_media: 0,
              total_avaliacoes: 0,
              tempo_resposta: 0,
              resolvidos: 0,
            };
          }
        })
      );
      setTribunais(tribunaisWithRatings);
    } catch (err) {
      console.error('Erro ao listar tribunais:', err);
    }
  };

  const getImagemUrl = (imagem) => {
    if (!imagem) return null;
    if (imagem.startsWith('/uploads/tribunais/')) {
      return `http://localhost:3001${imagem}`;
    }
    return `http://localhost:3001/uploads/tribunais/${imagem}`;
  };

  const getRatingClass = (rating) => {
    if (rating >= 7) return 'otimo';
    if (rating >= 5) return 'bom';
    return 'ruim';
  };

  const getRatingText = (rating) => {
    if (rating >= 7) return 'ÓTIMO';
    if (rating >= 5) return 'BOM';
    return 'RUIM';
  };

  const handleVisualizarClick = (id_tribunal) => {
    localStorage.setItem('id_tribunal', id_tribunal);
  };

  const filteredTribunais = tribunais.filter(tribunal => 
    tribunal.nome.toLowerCase().includes(searchTerm.toLowerCase()) || 
    tribunal.endereco.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container">
      <div className="search-container">
        <input
          type="text"
          placeholder="Buscar tribunal por nome ou endereço..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>

      <div className="categories-container">
        <div className="categories-wrapper">
          <button className="category-button active">Todos os Tribunais</button>
          <button className="category-button">Mais Bem Avaliados</button>
          <button className="category-button">Recentes</button>
          <button className="category-button">Problemas Resolvidos</button>
        </div>
      </div>

      <div className="tribunals-grid">
        {filteredTribunais.map(tribunal => (
          <Link
            key={tribunal.id_tribunal}
            to={`/user/dashboard/tribunais/${tribunal.id_tribunal}/feedback`}
            onClick={() => handleVisualizarClick(tribunal.id_tribunal)}
            className="tribunal-card"
          >
            <div className="tribunal-content">
              <div className="tribunal-image-container">
                {tribunal.imagem ? (
                  <img
                    src={getImagemUrl(tribunal.imagem)}
                    alt={tribunal.nome}
                    className="tribunal-image"
                  />
                ) : (
                  <div className="tribunal-no-image">Sem imagem</div>
                )}
              </div>

              <div className="tribunal-info">
                <h3 className="tribunal-name">{tribunal.nome}</h3>
                <p className="tribunal-city">{tribunal.cidade} - {tribunal.estado}</p>
                
                <div className="tribunal-status">
                  <div className="tribunal-rating">
                    <span 
                      className="rating-badge"
                      data-rating={getRatingClass(tribunal.avaliacao_media)}
                    >
                      {getRatingText(tribunal.avaliacao_media)}
                    </span>
                  </div>
                </div>

                <div className="tribunal-stats">
                  <div className="stat-item">
                    <span className="stat-label">Reclamações</span>
                    <span className="stat-value">{tribunal.total_avaliacoes}</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">Tempo médio resposta</span>
                    <span className="stat-value">{tribunal.tempo_resposta}h</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">Índice de solução</span>
                    <span className="stat-value">{tribunal.resolvidos}%</span>
                  </div>
                </div>
                
                <p className="tribunal-address">{tribunal.endereco}</p>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default TribunaisListPageUser;
