import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
import ContainerHome from '../../ContainerHome/index';

const ForunsListFeed = () => {
    const { id_forum } = useParams();
    const [comments, setComments] = useState([]);
    const [averageRating, setAverageRating] = useState(0);
    const [userId, setUserId] = useState(null); // Estado para armazenar o ID do usuário

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('usuarios'));
        if (storedUser) {
            setUserId(storedUser.id); // Define o ID do usuário vindo do localStorage
        }
    }, []);

    useEffect(() => {
        if (id_forum) {
            fetchComments();
            fetchAverageRating();
        }
    }, [id_forum]);

    const fetchComments = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/av_foruns/${id_forum}`);
            setComments(res.data);
        } catch (error) {
            console.error('Erro ao buscar comentários:', error);
        }
    };

    const fetchAverageRating = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/foruns_avaliacao/${id_forum}`);
            setAverageRating(res.data.media_avaliacao || 0);
        } catch (error) {
            console.error('Erro ao calcular média de avaliações:', error);
        }
    };

    return (
        <ContainerHome>
            <div>
                {id_forum && (
                    <Link to={`/user/dashboard/foruns/${id_forum}/feedback/add`}>
                        Realizar Feedback?
                    </Link>
                )}
                <h3>Média: ★ {averageRating}</h3>
                <h4>Comentários</h4>
                {comments.length > 0 ? (
                    comments.map((comment, index) => (
                        <div key={index}>
                            <p>
                                <strong>{comment.nome}</strong> - Anonimo{userId ? `ID: ${userId}` : ''}
                                Avaliação: ★ {comment.avaliacao}
                            </p>
                            <p>{comment.comentario}</p>
                            <p>Data: {new Date(comment.data_criacao).toLocaleDateString()}</p>
                        </div>
                    ))
                ) : (
                    <p>Ainda sem comentários.</p>
                )}
            </div>
        </ContainerHome>
    );
};

export default ForunsListFeed;
