import React, { useState } from 'react';

const ContainerLggd = ({ children, title = "titulo a ser posto", onSearch }) => {
    const [searchTerm, setSearchTerm] = useState('');

    const handleSearch = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
        if (onSearch) {
            onSearch(value);
        }
    };

    return (
        <section>
            <div className="w-full bg-gray-200 p-5 flex items-center justify-between">
                <div className="flex items-center bg-white rounded-full px-4 py-2 w-72 shadow-sm hover:shadow-md transition-shadow">
                    <input
                        type="text"
                        placeholder="Pesquisar..."
                        value={searchTerm}
                        onChange={handleSearch}
                        className="w-full border-none outline-none bg-transparent text-base"
                    />
                </div>
                <div className="ml-5 text-lg font-medium">{title}</div>
                <div className="w-96"></div>
            </div>
            <main className="p-6">
                {children}
            </main>
        </section>
    );
};

export default ContainerLggd;
