import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdvocaciaFilter = () => {
  const [advogados, setAdvogados] = useState([]);
  const [selectedFilter, setSelectedFilter] = useState('');

  // Buscar dados iniciais
  useEffect(() => {
    fetchAdvogados();
  }, []);

  // Função para buscar todos os advogados
  const fetchAdvogados = async () => {
    try {
      const response = await axios.get('/advocacia');
      setAdvogados(response.data);
    } catch (error) {
      console.error('Erro ao buscar dados:', error);
    }
  };

  // Função para buscar por profissão específica
  const fetchByProfissao = async (profissao) => {
    try {
      const response = await axios.get(`/advocacia/profissao/${profissao}`);
      setAdvogados(response.data);
    } catch (error) {
      console.error('Erro ao buscar por profissão:', error);
    }
  };

  // Função para lidar com a mudança no dropdown
  const handleFilterChange = async (e) => {
    const value = e.target.value;
    setSelectedFilter(value);

    if (value === '') {
      await fetchAdvogados();
    } else {
      await fetchByProfissao(value);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <div className="mb-4">
        <select
          value={selectedFilter}
          onChange={handleFilterChange}
          className="w-full p-2 border rounded-md shadow-sm bg-white"
        >
          <option value="">Selecione...</option>
          <option value="Escritório">Escritórios</option>
          <option value="Advogado">Advogados</option>
          <option value="Promotor">Promotor</option>
        </select>
      </div>

      {/* Lista de profissionais filtrados */}
      <div className="grid gap-4">
        {advogados.length > 0 ? (
          advogados.map((profissional) => (
            <div 
              key={profissional.id_advocacia}
              className="p-4 border rounded-lg shadow-sm bg-white"
            >
              <h3 className="font-bold text-lg mb-2">{profissional.nome}</h3>
              <p className="mb-1"><span className="font-semibold">Profissão:</span> {profissional.profissao}</p>
              <p className="mb-1"><span className="font-semibold">Escritório:</span> {profissional.escritorio}</p>
              <p className="mb-1"><span className="font-semibold">Experiência:</span> {profissional.experiencia}</p>
              <p className="mb-1"><span className="font-semibold">Endereço:</span> {profissional.endereco}</p>
              {profissional.imagem && (
                <img 
                  src={profissional.imagem} 
                  alt={profissional.nome}
                  className="mt-2 rounded-md w-32 h-32 object-cover"
                />
              )}
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">Nenhum profissional encontrado</p>
        )}
      </div>
    </div>
  );
};

export default AdvocaciaFilter;
