import { Link, useLocation } from "react-router-dom";
import styles from "./HeaderLggd.module.css";

function HeaderLggd({children}) {
    const location = useLocation();
    
    // Array com as rotas disponíveis
    const navigation = [
        { name: 'Tribunais', path: '/Tribunais' },
        { name: 'Fóruns', path: '/Foruns' },
        { name: 'C', path: '/configuracoes' },
        // Adicione mais rotas conforme necessário
    ];

    return (
        <header>
            <nav className={styles.navbar}>
                <div className={styles.navleft}>
                    <Link className={styles.usuario}>U</Link>
                </div>
                
                {/* Botões de navegação */}
                <div className={styles.navButtons}>
                    {navigation.map((item) => (
                        <Link
                            key={item.path}
                            to={item.path}
                            className={`${styles.navButton} ${
                                location.pathname === item.path ? styles.active : ''
                            }`}
                            // Desativa o botão se estiver na página atual
                            onClick={(e) => {
                                if (location.pathname === item.path) {
                                    e.preventDefault();
                                }
                            }}
                        >
                            {item.name}
                        </Link>
                    ))}
                </div>
                
                {children}
                <span className={styles.brand}>JUSTIX</span>
            </nav>
        </header>
    );
}

export default HeaderLggd;
