// CarouselCustom.js
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const CarouselCustom = ({ name, itemsPerSlide = 3, carouselItems }) => {
    const createCarouselSlides = (items, itemsPerSlide) => {
        const slides = [];
        for (let i = 0; i < items.length; i += itemsPerSlide) {
            slides.push(items.slice(i, i + itemsPerSlide));
        }
        return slides;
    };

    const slides = createCarouselSlides(carouselItems, itemsPerSlide);

    return (
        <div className="container my-5">
            <h2>{name}</h2>
            <div id="customCarousel" className="carousel slide" data-bs-ride="carousel">
                <div className="carousel-inner">
                    {slides.map((slide, index) => (
                        <div key={index} className={`carousel-item ${index === 0 ? 'active' : ''}`}>
                            <div className="row">
                                {slide.map((item, idx) => (
                                    <div key={idx} className="col-md-4">
                                        <div className="card text-center mb-4">
                                            {item.image && (
                                                <img src={item.image} className="card-img-top" alt={item.title} />
                                            )}
                                            <div className="card-body">
                                                <h5 className="card-title">{item.title}</h5>
                                                <p className="card-text">{item.subtitle}</p>
                                                {item.link && (
                                                    <a href={item.link} className="btn btn-primary">
                                                        Ver Mais
                                                    </a>
                                                )}
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}
                </div>
                <button
                    className="carousel-control-prev"
                    type="button"
                    data-bs-target="#customCarousel"
                    data-bs-slide="prev"
                >
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Anterior</span>
                </button>
                <button
                    className="carousel-control-next"
                    type="button"
                    data-bs-target="#customCarousel"
                    data-bs-slide="next"
                >
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Próximo</span>
                </button>
            </div>
        </div>
    );
};

export default CarouselCustom;
