import React, { useState, useEffect, useRef } from 'react';
import styles from "./HeaderLggd.module.css";

function HeaderLggd({ children }) {
    const [isOpen, setIsOpen] = useState(false);
    const [showUserDetails, setShowUserDetails] = useState(false);
    const popupRef = useRef(null);
    const [user, setUser] = useState(null);
    const [firstLetter, setFirstLetter] = useState('');

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('usuarios'));
        if (storedUser) {
            setUser(storedUser);
            setFirstLetter(storedUser.nome.charAt(0).toUpperCase());
        }
    }, []);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (popupRef.current && !popupRef.current.contains(event.target)) {
                setIsOpen(false);
                setShowUserDetails(false); // Fechar detalhes do usuário ao clicar fora
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('usuarios');
        window.location.href = '/login';
    };

    const toggleUserDetails = () => {
        setShowUserDetails(!showUserDetails);
    };

    return (
        <header>
            <nav className={styles.navbar}>
                <div className={styles.navleft} ref={popupRef}>
                    <button
                        className={styles.usuario}
                        onClick={() => setIsOpen(!isOpen)}
                    >
                        {firstLetter}
                    </button>

                    {isOpen && (
                        <div className={styles.popup}>
                            <div className={styles.popupLink}>
                                {user?.nome}
                            </div>
                            
                            {user?.role !== 'admin' ? (
                                <>
                                    <div
                                        onClick={toggleUserDetails}
                                        className={styles.popupLink}
                                    >
                                        Dados do usuário
                                    </div>
                                    {showUserDetails && (
                                        <div className={styles.userDetails}>
                                            <p>Email: {user?.email}</p>
                                            <p>Telefone: {user?.telefone}</p>
                                            <p>CPF: {user?.cpf}</p>
                                        </div>
                                    )}
                                    <div className={styles.popupLink}>
                                        Pontuação
                                    </div>
                                </>
                            ) : null}
                            
                            <button
                                onClick={handleLogout}
                                className={styles.logoutButton}
                            >
                                Logout
                            </button>
                        </div>
                    )}
                </div>
                {children}
                <span className={styles.brand}>JUSTIX</span>
            </nav>
        </header>
    );
}

export default HeaderLggd;
