import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

const HeaderLggd = ({ children }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [showDetails, setShowDetails] = useState(false);
    const [showProgress, setShowProgress] = useState(false);
    const [isMobileView, setIsMobileView] = useState(false);
    const popupRef = useRef(null);
    const searchRef = useRef(null);
    const [user, setUser] = useState(null);
    const [firstLetter, setFirstLetter] = useState('');
    const [userColor, setUserColor] = useState('#000000');
    const [progress, setProgress] = useState(0);
    const [rewardToken, setRewardToken] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const generateRandomToken = () => {
        const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        const length = 16;
        let token = '';
        for (let i = 0; i < length; i++) {
            token += characters.charAt(Math.floor(Math.random() * characters.length));
        }
        setProgress(0); // Reset progress after generating token
        return token;
    };

    const fetchSearchResults = async (query) => {
        try {
            const endpoints = [
                { name: 'tribunais', idField: 'id_tribunal' },
                { name: 'foruns', idField: 'id_forum' },
                { name: 'juiz', idField: 'id_juiz' },
                { name: 'mediador', idField: 'id_mediador' },
                { name: 'advocacia', idField: 'id_advocacia' },
                { name: 'portais', idField: 'id_portal' }
            ];

            const results = await Promise.all(endpoints.map(async ({ name, idField }) => {
                const response = await fetch(`http://localhost:3001/${name}`);
                const data = await response.json();
                return data.map(item => ({
                    name: item.nome,
                    link: `/user/${name}/${item[idField]}/feedback`,
                }));
            }));

            const allItems = results.flat();
            const filteredItems = allItems.filter(item =>
                item.name.toLowerCase().includes(query.toLowerCase())
            );
            setSearchResults(filteredItems);
        } catch (error) {
            console.error('Erro ao buscar dados:', error);
        }
    };

    const handleSearch = (e) => {
        e.preventDefault();
        if (searchQuery) fetchSearchResults(searchQuery);
    };

    const handleInputChange = (e) => {
        const query = e.target.value;
        setSearchQuery(query);
        setIsMenuOpen(!!query);
        if (query) {
            fetchSearchResults(query);
        } else {
            setSearchResults([]);
        }
    };

    const handleGenerateToken = () => {
        const token = generateRandomToken();
        setRewardToken(token);
        localStorage.setItem('rewardToken', token);
    };

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('usuarios'));
        if (storedUser) {
            setUser(storedUser);
            setFirstLetter(storedUser.nome.charAt(0).toUpperCase());
            
            let userColor = localStorage.getItem('userColor');
            if (!userColor) {
                userColor = generateRandomColor();
                localStorage.setItem('userColor', userColor);
            }
            setUserColor(userColor);

            const storedProgress = localStorage.getItem(`userProgress_${storedUser.id}`);
            setProgress(storedProgress ? parseInt(storedProgress) : 0);
        }

        const checkMobileView = () => {
            setIsMobileView(window.innerWidth <= 768);
        };

        checkMobileView();
        window.addEventListener('resize', checkMobileView);
        return () => window.removeEventListener('resize', checkMobileView);
    }, []);

    const generateRandomColor = () => {
        const colors = [
            '#2196F3', '#4CAF50', '#9C27B0', '#F44336', '#FF9800',
            '#009688', '#3F51B5', '#795548', '#607D8B', '#E91E63'
        ];
        return colors[Math.floor(Math.random() * colors.length)];
    };

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (popupRef.current && !popupRef.current.contains(event.target)) {
                setIsOpen(false);
            }
            if (searchRef.current && !searchRef.current.contains(event.target)) {
                setIsMenuOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('usuarios');
        localStorage.removeItem('userColor');
        window.location.href = '/';
    };

    // Sidebar/Popup Content Component
    const UserMenu = () => (
        <div className={`${isMobileView ? 'fixed inset-0 bg-black bg-opacity-50 z-50' : ''}`} onClick={() => !isMobileView && setIsOpen(false)}>
            <div className={`
                ${isMobileView 
                    ? 'fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-white rounded-lg shadow-lg w-11/12 max-w-md' 
                    : 'fixed top-0 right-0 h-full bg-white shadow-lg w-80'}
                transition-all duration-300 z-50`
            }>
                <div className="p-6 space-y-4">
                    <div className="flex items-center space-x-4 mb-6">
                        <div className="w-12 h-12 rounded-full flex items-center justify-center text-white text-xl font-bold" 
                             style={{ backgroundColor: userColor }}>
                            {firstLetter}
                        </div>
                        <div className="text-lg font-semibold">{user?.nome}</div>
                    </div>

                    <button onClick={() => setShowDetails(!showDetails)} 
                            className="w-full text-left py-3 px-4 hover:bg-gray-100 rounded-lg transition-colors">
                        Dados do usuário
                    </button>

                    {showDetails && (
                        <div className="bg-gray-50 p-4 rounded-lg space-y-2 text-sm">
                            <div>CPF: {user?.cpf || 'Não disponível'}</div>
                            <div>Telefone: {user?.telefone || 'Não disponível'}</div>
                            <div>Email: {user?.email || 'Não disponível'}</div>
                        </div>
                    )}

                    <button onClick={() => setShowProgress(!showProgress)}
                            className="w-full text-left py-3 px-4 hover:bg-gray-100 rounded-lg transition-colors">
                        Pontuação
                    </button>

                    {showProgress && (
                        <div className="bg-gray-50 p-4 rounded-lg space-y-4">
                            <div className="w-full bg-gray-200 rounded-full h-4 overflow-hidden">
                                <div className="h-full bg-blue-500 transition-all duration-300"
                                     style={{ width: `${(progress / 1000) * 100}%` }}>
                                </div>
                            </div>
                            <div className="text-center text-sm font-medium">
                                {progress} / 1000 pontos
                            </div>
                            {progress >= 1000 && !rewardToken && (
                                <button onClick={handleGenerateToken}
                                        className="w-full py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors">
                                    Gerar Token de Recompensa
                                </button>
                            )}
                            {rewardToken && (
                                <div className="p-3 bg-gray-100 rounded-lg text-center break-all">
                                    <p className="text-xs font-medium mb-1">Seu Token:</p>
                                    <p className="text-sm">{rewardToken}</p>
                                </div>
                            )}
                        </div>
                    )}

                    <button onClick={handleLogout}
                            className="w-full text-left py-3 px-4 hover:bg-gray-100 rounded-lg transition-colors text-red-600">
                        Logout
                    </button>
                </div>
            </div>
        </div>
    );

    return (
        <header className="fixed top-0 w-full z-50">
            <nav className="bg-gray-200 py-4 px-6 flex items-center justify-between shadow-md">
                <Link to="/user/" className="text-blue-600 text-2xl font-bold no-underline">
                    JUSTIX
                </Link>

                <div className="flex-1 max-w-2xl mx-8" ref={searchRef}>
                    <form onSubmit={handleSearch} className="w-full">
                        <div className="flex">
                            <input
                                type="text"
                                placeholder="Pesquisar..."
                                value={searchQuery}
                                onChange={handleInputChange}
                                className="flex-1 px-4 py-2 border-2 border-gray-300 rounded-l-lg focus:border-blue-500 outline-none"
                            />
                            <button type="submit" 
                                    className="px-6 py-2 bg-blue-600 text-white rounded-r-lg hover:bg-blue-700 transition-colors">
                                Buscar
                            </button>
                        </div>
                    </form>

                    {isMenuOpen && searchResults.length > 0 && (
                        <div className="absolute mt-1 w-full bg-white rounded-lg shadow-lg max-h-96 overflow-y-auto">
                            <ul className="py-2">
                                {searchResults.map((result, index) => (
                                    <li key={index} className="px-4 py-2 hover:bg-gray-100">
                                        <Link to={result.link} 
                                              className="block text-gray-700 no-underline hover:text-blue-600"
                                              onClick={() => setIsMenuOpen(false)}>
                                            {result.name}
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>

                <div className="flex items-center space-x-6">
                    <div className="relative"
                         onMouseEnter={() => setIsDropdownOpen(true)}
                         onMouseLeave={() => setIsDropdownOpen(false)}>
                        <button className="text-gray-700 font-medium hover:text-blue-600 transition-colors">
                            Opções de busca
                        </button>
                        {isDropdownOpen && (
                            <div className="absolute top-full right-0 mt-2 w-48 bg-white rounded-lg shadow-lg py-2">
                                <Link to="/user/tribunais" className="block px-4 py-2 text-gray-700 hover:bg-gray-100">Tribunais</Link>
                                <Link to="/user/foruns" className="block px-4 py-2 text-gray-700 hover:bg-gray-100">Fóruns</Link>
                                <Link to="/user/juiz" className="block px-4 py-2 text-gray-700 hover:bg-gray-100">Audiências</Link>
                                <Link to="/user/mediacoes" className="block px-4 py-2 text-gray-700 hover:bg-gray-100">Mediações</Link>
                                <Link to="/user/advocacia" className="block px-4 py-2 text-gray-700 hover:bg-gray-100">Advocacia</Link>
                                <Link to="/user/portais" className="block px-4 py-2 text-gray-700 hover:bg-gray-100">Portais</Link>
                            </div>
                        )}
                    </div>

                    <button onClick={() => setIsOpen(true)}
                            className="w-10 h-10 rounded-full text-white font-bold flex items-center justify-center"
                            style={{ backgroundColor: userColor }}>
                        {firstLetter}
                    </button>
                </div>
            </nav>
            {isOpen && <UserMenu />}
            {children}
        </header>
    );
};

export default HeaderLggd;
