import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Cadastro.module.css';

function Cadastro() {
    const navigate = useNavigate();
    const sidebarRef = useRef(null); // Referência para o sidebar
    const [formData, setFormData] = useState({
        nome: '',
        cpf: '',
        email: '',
        telefone: '',
        senha: '',
        confirmarSenha: ''
    });
    
    const [errors, setErrors] = useState({});
    const [submitStatus, setSubmitStatus] = useState({});
    const [isSidebarVisible, setSidebarVisible] = useState(false); // Estado para controlar a visibilidade do sidebar

    // Função para formatar CPF
    const formatCPF = (value) => value.replace(/\D/g, '')
        .replace(/(\d{3})(\d)/, '$1.$2')
        .replace(/(\d{3})(\d)/, '$1.$2')
        .replace(/(\d{3})(\d{1,2})/, '$1-$2')
        .replace(/(-\d{2})\d+?$/, '$1');

    // Função para formatar Telefone
    const formatTelefone = (value) => value.replace(/\D/g, '')
        .replace(/(\d{2})(\d)/, '($1) $2')
        .replace(/(\d{5})(\d)/, '$1-$2')
        .replace(/(-\d{4})\d+?$/, '$1');

    // Validação dos campos
    const validateField = (name, value) => {
        switch (name) {
            case 'nome':
                return value.length < 3 ? 'Nome deve ter pelo menos 3 caracteres' : '';
            case 'cpf':
                return value.replace(/\D/g, '').length !== 11 ? 'CPF deve ter 11 dígitos' : '';
            case 'email':
                return !value.includes('@') || !value.includes('.com') ? 'Email deve conter @ e .com' : '';
            case 'telefone':
                return value.replace(/\D/g, '').length < 11 ? 'Telefone deve ter 11 dígitos' : '';
            case 'senha':
                return value.length < 6 ? 'Senha deve ter pelo menos 6 caracteres' : '';
            case 'confirmarSenha':
                return value !== formData.senha ? 'As senhas não coincidem' : '';
            default:
                return '';
        }
    };

    // Atualiza os valores dos campos e realiza a validação
    const handleInputChange = (e) => {
        const { id, value } = e.target;
        const formattedValue = id === 'cpf' ? formatCPF(value) : id === 'telefone' ? formatTelefone(value) : value;
        setFormData((prev) => ({ ...prev, [id]: formattedValue }));
        
        const error = validateField(id, formattedValue);
        setErrors((prev) => ({ ...prev, [id]: error }));
    };

    // Lida com o envio do formulário
    const handleSubmit = async (e) => {
        e.preventDefault();
        const hasErrors = Object.values(errors).some(error => error);

        if (hasErrors) {
            setSubmitStatus({ message: 'Por favor, corrija os erros no formulário.', type: 'error' });
            return;
        }

        try {
            const response = await fetch('http://localhost:3001/usuarios', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    nome: formData.nome,
                    cpf: formData.cpf.replace(/\D/g, ''),
                    email: formData.email,
                    telefone: formData.telefone.replace(/\D/g, ''),
                    senha: formData.senha
                })
            });

            const data = await response.json();
            if (response.ok) {
                setSubmitStatus({ message: 'Cadastro realizado com sucesso!', type: 'success' });
                setTimeout(() => navigate('/login'), 2000);
            } else {
                throw new Error(data.error || 'Erro ao realizar cadastro');
            }
        } catch (error) {
            setSubmitStatus({ message: error.message || 'Erro ao conectar com o servidor', type: 'error' });
        }
    };

    // Função para alternar a visibilidade do sidebar
    const toggleSidebar = () => setSidebarVisible(!isSidebarVisible);

    // Hook para lidar com cliques fora do sidebar
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (sidebarRef.current && !sidebarRef.current.contains(event.target)) {
                setSidebarVisible(false);
            }
        };

        // Adiciona o evento de clique
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    // Verifica o tamanho da tela para exibir o sidebar ou popup
    const isMobile = window.innerWidth < 768;

    return (
        <>
            {isMobile ? (
                <div className={`${styles.popupContainer} ${isSidebarVisible ? styles.popupVisible : ''}`}>
                    <div className={styles.popupContent}>
                        <h2 className={styles.title}>Cadastro</h2>

                        {submitStatus.message && (
                            <div className={`${styles.statusMessage} ${styles[submitStatus.type]}`}>
                                {submitStatus.message}
                            </div>
                        )}

                        <form onSubmit={handleSubmit} className={styles.cadastroForm}>
                            {/* Campos do formulário */}
                        </form>

                        <button className={styles.closeButton} onClick={toggleSidebar}>
                            Fechar
                        </button>
                    </div>
                </div>
            ) : (
                <div className={`${styles.sidebarContainer} ${isSidebarVisible ? styles.sidebarVisible : ''}`} ref={sidebarRef}>
                    <section className={styles.cadastroSidebar}>
                        <h2 className={styles.title}>Cadastro</h2>

                        {submitStatus.message && (
                            <div className={`${styles.statusMessage} ${styles[submitStatus.type]}`}>
                                {submitStatus.message}
                            </div>
                        )}

                        <form onSubmit={handleSubmit} className={styles.cadastroForm}>
                            {/* Campos do formulário */}
                        </form>
                    </section>
                </div>
            )}
        </>
    );
}

export default Cadastro;
