import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function FeedbackPage() {
    const { entityType, id } = useParams();
    const [entidade, setEntidade] = useState(null);
    const [mediaAvaliacao, setMediaAvaliacao] = useState(0);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Buscar dados da entidade
                const entidadeRes = await axios.get(`http://localhost:3001/${entityType}/${id}`);
                setEntidade(entidadeRes.data);

                // Buscar m√©dia de avalia√ß√£o
                const ratingRes = await axios.get(`http://localhost:3001/${entityType}_avaliacao/${id}`);
                setMediaAvaliacao(ratingRes.data.media_avaliacao || 0);
            } catch (error) {
                console.error('Erro ao buscar dados:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [entityType, id]);

    if (loading) return <p>Carregando...</p>;

    return (
        <div className="feedback-page">
            {entidade ? (
                <div>
                    <h1>{entidade.nome}</h1>
                    <p>{entidade.descricao}</p>
                    <p>üìç Localiza√ß√£o: {entidade.localizacao || 'N√£o especificado'}</p>
                    <p>‚≠ê Avalia√ß√£o m√©dia: {mediaAvaliacao.toFixed(1)}</p>
                </div>
            ) : (
                <p>Entidade n√£o encontrada.</p>
            )}
        </div>
    );
}

export default FeedbackPage;
