import React, { useState, useEffect } from 'react';
import axios from 'axios';

const HomeCards = () => {
  const [tribunais, setTribunais] = useState([]);
  const [foruns, setForuns] = useState([]);
  const [juizes, setJuizes] = useState([]);
  const [mediadores, setMediadores] = useState([]);
  const [advocacia, setAdvocacia] = useState([]);
  const [portais, setPortais] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        await Promise.all([
          listarEntidades('tribunais', setTribunais, 'tribunais_avaliacao'),
          listarEntidades('foruns', setForuns),
          listarEntidades('juiz', setJuizes, 'juiz_avaliacao'),
          listarEntidades('mediador', setMediadores),
          listarEntidades('advocacia', setAdvocacia),
          listarEntidades('portais', setPortais),
        ]);
      } catch (error) {
        console.error('Erro ao carregar dados:', error);
      }
    };

    fetchData();
  }, []);

  const listarEntidades = async (endpoint, setData, ratingEndpoint) => {
    try {
      const res = await axios.get(`http://localhost:3001/${endpoint}`);
      const entidadesWithRatings = await Promise.all(
        res.data.map(async (entidade) => {
          if (ratingEndpoint) {
            try {
              const ratingRes = await axios.get(`http://localhost:3001/${ratingEndpoint}/${entidade.id}`);
              const mediaAvaliacao = parseFloat(ratingRes.data.media_avaliacao) || 0;
              return {
                ...entidade,
                avaliacao_media: mediaAvaliacao
              };
            } catch (err) {
              console.error(`Erro ao buscar avaliação para ${endpoint} ${entidade.id}:`, err);
              return {
                ...entidade,
                avaliacao_media: 0
              };
            }
          }
          return entidade;
        })
      );
      setData(entidadesWithRatings);
    } catch (err) {
      console.error(`Erro ao listar ${endpoint}:`, err);
    }
  };

  const formatRating = (rating) => {
    const numRating = parseFloat(rating);
    return isNaN(numRating) ? "0.0" : numRating.toFixed(1);
  };

  const getImageUrl = (type, imagem) => {
    if (!imagem) return null;
    return `http://localhost:3001/uploads/${type}/${imagem}`;
  };

  const Card = ({ item, type }) => {
    const getRatingColor = (rating) => {
      if (rating >= 8) return 'text-green-600';
      if (rating >= 6) return 'text-yellow-600';
      return 'text-red-600';
    };

    return (
      <div className="bg-white rounded-lg shadow-lg p-6 hover:shadow-xl transition-shadow">
        <div className="flex items-center mb-4">
          {item.imagem && (
            <img 
              src={getImageUrl(type, item.imagem)} 
              alt={item.nome} 
              className="w-16 h-16 rounded-full object-cover mr-4"
            />
          )}
          <div>
            <h3 className="text-xl font-bold mb-1">{item.nome}</h3>
            <div className={`text-2xl font-bold ${getRatingColor(item.avaliacao_media)}`}>
              {formatRating(item.avaliacao_media)}
              <span className="text-sm text-gray-500">/10</span>
            </div>
          </div>
        </div>
        
        <div className="border-t pt-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="text-center">
              <div className="text-sm text-gray-500">Resolvidos</div>
              <div className="font-bold text-lg">
                {item.avaliacao_media}
              </div>
            </div>
            <div className="text-center">
              <div className="text-sm text-gray-500">Avaliações</div>
              <div className="font-bold text-lg">{Math.floor(Math.random() * 1000)}</div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const CardSection = ({ title, items, type }) => (
    <div className="mb-8">
      <h2 className="text-2xl font-bold mb-4">{title}</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {items.slice(0, 3).map((item, index) => (
          <Card key={item.id || index} item={item} type={type} />
        ))}
      </div>
    </div>
  );

  return (
    <div className="container mx-auto px-4 py-8">
      <CardSection title="Tribunais em Destaque" items={tribunais} type="tribunais" />
      <CardSection title="Fóruns em Destaque" items={foruns} type="foruns" />
      <CardSection title="Juízes em Destaque" items={juizes} type="juiz" />
      <CardSection title="Mediadores em Destaque" items={mediadores} type="mediador" />
      <CardSection title="Advocacias em Destaque" items={advocacia} type="advocacia" />
      <CardSection title="Portais em Destaque" items={portais} type="portais" />
    </div>
  );
};

export default HomeCards;
