import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Star, MessageCircle, ThumbsUp, ThumbsDown, Clock, BarChart2 } from 'lucide-react';
import './TribunalProfile.css';

const API_BASE_URL = 'http://localhost:3001';

const TribunalProfile = () => {
    const { id_tribunal } = useParams();
    const [tribunal, setTribunal] = useState(null);
    const [ratings, setRatings] = useState(null);
    const [comments, setComments] = useState([]);
    const [selectedPeriod, setSelectedPeriod] = useState('all');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const ratingCategories = {
        av_eficiencia: { name: 'Eficiência', weight: 5 },
        av_qualidade: { name: 'Qualidade', weight: 4 },
        av_infraestrutura: { name: 'Infraestrutura', weight: 3 },
        av_tecnologia: { name: 'Tecnologia', weight: 3 },
        av_gestao: { name: 'Gestão', weight: 2 },
        av_transparencia: { name: 'Transparência', weight: 2 },
        av_sustentabilidade: { name: 'Sustentabilidade', weight: 1 }
    };

    useEffect(() => {
        fetchTribunalData();
    }, [id_tribunal]);

    const fetchTribunalData = async () => {
        try {
            const [tribunalRes, ratingsRes, commentsRes] = await Promise.all([
                axios.get(`${API_BASE_URL}/tribunais/${id_tribunal}`),
                axios.get(`${API_BASE_URL}/tribunais_avaliacao/${id_tribunal}`),
                axios.get(`${API_BASE_URL}/av_tribunais/${id_tribunal}`)
            ]);

            setTribunal(tribunalRes.data);
            setRatings(ratingsRes.data);
            setComments(commentsRes.data);
            setLoading(false);
        } catch (err) {
            console.error('Error fetching tribunal data:', err);
            setError('Falha ao carregar os dados do tribunal. Tente novamente mais tarde.');
            setLoading(false);
        }
    };

    const getImageUrl = (imagem) => {
        if (!imagem) return null;
        if (imagem.startsWith('/uploads/')) {
            return `${API_BASE_URL}${imagem}`;
        }
        return `${API_BASE_URL}/uploads/tribunais/${imagem}`;
    };

    const calculateCategoryRating = (comments, category) => {
        if (!comments.length) return 0;
        const sum = comments.reduce((acc, comment) => acc + (comment[category] || 0), 0);
        return sum / comments.length;
    };

    const getReputationStatus = (rating) => {
        if (rating >= 4) return { label: 'Ótimo', class: 'reputation-excellent' };
        if (rating >= 3) return { label: 'Bom', class: 'reputation-good' };
        if (rating >= 2) return { label: 'Regular', class: 'reputation-regular' };
        return { label: 'Ruim', class: 'reputation-bad' };
    };

    const filterCommentsByPeriod = (comments) => {
        const now = new Date();
        switch (selectedPeriod) {
            case '6months':
                return comments.filter(comment => 
                    new Date(comment.data_criacao) > new Date(now.setMonth(now.getMonth() - 6))
                );
            case '3months':
                return comments.filter(comment => 
                    new Date(comment.data_criacao) > new Date(now.setMonth(now.getMonth() - 3))
                );
            case '1month':
                return comments.filter(comment => 
                    new Date(comment.data_criacao) > new Date(now.setMonth(now.getMonth() - 1))
                );
            default:
                return comments;
        }
    };

    if (loading) {
        return <div className="loading">Carregando dados do tribunal...</div>;
    }

    if (error) {
        return <div className="error">{error}</div>;
    }

    const filteredComments = filterCommentsByPeriod(comments);
    const reputationStatus = getReputationStatus(ratings?.media_ponderada || 0);

    return (
        <div className="tribunal-profile">
            <header className="profile-header">
                <div className="profile-header-content">
                    <div className="tribunal-basic-info">
                        {tribunal?.imagem ? (
                            <img 
                                src={getImageUrl(tribunal.imagem)} 
                                alt={tribunal.nome}
                                className="tribunal-logo"
                            />
                        ) : (
                            <div className="tribunal-logo-placeholder">
                                {tribunal?.nome?.charAt(0)}
                            </div>
                        )}
                        <div>
                            <h1>{tribunal?.nome}</h1>
                            <p className="tribunal-location">
                                {tribunal?.cidade} - {tribunal?.estado}
                            </p>
                        </div>
                    </div>
                    <div className="tribunal-reputation">
                        <div className={`reputation-score ${reputationStatus.class}`}>
                            <span className="score-value">
                                {(ratings?.media_ponderada || 0).toFixed(1)}
                            </span>
                            <Star className="score-star" />
                        </div>
                        <span className="reputation-label">{reputationStatus.label}</span>
                        <div className="total-reviews">
                            <MessageCircle size={16} />
                            {comments.length} avaliações
                        </div>
                    </div>
                </div>
            </header>

            <div className="profile-content">
                <div className="content-grid">
                    <section className="ratings-section">
                        <h2>Índices de Avaliação</h2>
                        <div className="period-filter">
                            <select 
                                value={selectedPeriod}
                                onChange={(e) => setSelectedPeriod(e.target.value)}
                            >
                                <option value="all">Todo o período</option>
                                <option value="6months">Últimos 6 meses</option>
                                <option value="3months">Últimos 3 meses</option>
                                <option value="1month">Último mês</option>
                            </select>
                        </div>

                        <div className="ratings-metrics">
                            {Object.entries(ratingCategories).map(([key, { name, weight }]) => {
                                const rating = calculateCategoryRating(filteredComments, key);
                                const status = getReputationStatus(rating);
                                return (
                                    <div key={key} className="metric-item">
                                        <div className="metric-header">
                                            <span className="metric-name">{name}</span>
                                            <span className="metric-weight">Peso {weight}</span>
                                        </div>
                                        <div className="metric-bar-container">
                                            <div 
                                                className={`metric-bar ${status.class}`}
                                                style={{ width: `${(rating / 5) * 100}%` }}
                                            />
                                            <span className="metric-value">
                                                {rating.toFixed(1)}
                                            </span>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                    </section>

                    <section className="stats-section">
                        <h2>Estatísticas</h2>
                        <div className="stats-grid">
                            <div className="stat-card">
                                <ThumbsUp size={20} />
                                <div className="stat-info">
                                    <span className="stat-value">
                                        {filteredComments.filter(c => 
                                            (c.av_eficiencia + c.av_qualidade) / 2 >= 4
                                        ).length}
                                    </span>
                                    <span className="stat-label">Avaliações positivas</span>
                                </div>
                            </div>
                            <div className="stat-card">
                                <ThumbsDown size={20} />
                                <div className="stat-info">
                                    <span className="stat-value">
                                        {filteredComments.filter(c => 
                                            (c.av_eficiencia + c.av_qualidade) / 2 < 3
                                        ).length}
                                    </span>
                                    <span className="stat-label">Avaliações negativas</span>
                                </div>
                            </div>
                            <div className="stat-card">
                                <Clock size={20} />
                                <div className="stat-info">
                                    <span className="stat-value">
                                        {filteredComments.length > 0 ? 
                                            `${Math.round(filteredComments.reduce((acc, c) => 
                                                acc + (c.av_eficiencia || 0), 0) / filteredComments.length * 20)}min` : 
                                            'N/A'}
                                    </span>
                                    <span className="stat-label">Tempo médio de resposta</span>
                                </div>
                            </div>
                            <div className="stat-card">
                                <BarChart2 size={20} />
                                <div className="stat-info">
                                    <span className="stat-value">
                                        {filteredComments.length > 0 
                                            ? ((filteredComments.filter(c => 
                                                (c.av_eficiencia + c.av_qualidade) / 2 >= 4
                                            ).length / filteredComments.length) * 100).toFixed(1)
                                            : '0.0'}%
                                    </span>
                                    <span className="stat-label">Avaliações positivas (%)</span>
                                </div>
                            </div>
                        </div>
                    </section>
                </div>
            </div>
        </div>
    );
};

export default TribunalProfile;
