// ForunsForm.js
import React, { useState } from 'react';
import axios from 'axios';

const ForunsForm = ({ id_forum, onCommentAdded }) => {
    const [newComment, setNewComment] = useState('');
    const [rating, setRating] = useState(1);
    const [protocolNumber, setProtocolNumber] = useState('');
    const [arrivalTime, setArrivalTime] = useState('');
    const [departureTime, setDepartureTime] = useState('');

    const handleSubmitComment = async (e) => {
        e.preventDefault();

        if (protocolNumber.length < 5 || protocolNumber.length > 20) {
            alert('O número de protocolo deve ter entre 5 e 20 dígitos.');
            return;
        }

        const id_usuario = localStorage.getItem('id_usuario'); // Obtém o ID do usuário do localStorage

        try {
            await axios.post('http://localhost:3001/av_foruns', {
                id_usuario,
                id_forum,
                numero_protocolo: protocolNumber,
                comentario: newComment,
                avaliacao: rating,
                horario_chegada: arrivalTime || null,
                horario_saida: departureTime || null,
            });

            setNewComment('');
            setRating(1);
            setProtocolNumber('');
            setArrivalTime('');
            setDepartureTime('');

            // Chama a função passada por props para atualizar a lista de comentários
            if (onCommentAdded) onCommentAdded();

        } catch (error) {
            console.error('Erro ao adicionar comentário:', error);
        }
    };

    return (
        <div>
            <h4>Deixe seu Comentário</h4>
            <form onSubmit={handleSubmitComment}>
                <label>
                    Número de Protocolo:
                    <input
                        type="text"
                        value={protocolNumber}
                        onChange={(e) => setProtocolNumber(e.target.value)}
                        required
                        minLength="5"
                        maxLength="20"
                    />
                </label>
                <label>
                    Comentário:
                    <textarea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                    />
                </label>
                <label>
                    Avaliação:
                    <select value={rating} onChange={(e) => setRating(e.target.value)}>
                        {[1, 2, 3, 4, 5].map((star) => (
                            <option key={star} value={star}>
                                {star}
                            </option>
                        ))}
                    </select>
                </label>
                <label>
                    Horário de Chegada (Opcional):
                    <input
                        type="time"
                        value={arrivalTime}
                        onChange={(e) => setArrivalTime(e.target.value)}
                    />
                </label>
                <label>
                    Horário de Saída (Opcional):
                    <input
                        type="time"
                        value={departureTime}
                        onChange={(e) => setDepartureTime(e.target.value)}
                    />
                </label>
                <button type="submit">Enviar Comentário</button>
            </form>
        </div>
    );
};

export default ForunsForm;
