import React, { useState, useEffect, useRef } from 'react';
import { Link } from "react-router-dom";
import { createPortal } from "react-dom";
import styles from "./HeaderLggd.module.css";

function HeaderLggd({ children }) {
    const [isOpen, setIsOpen] = useState(false);
    const [showDetails, setShowDetails] = useState(false);
    const popupRef = useRef(null);
    const [user, setUser] = useState(null);
    const [firstLetter, setFirstLetter] = useState('');

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('usuarios'));
        if (storedUser) {
            setUser(storedUser);
            setFirstLetter(storedUser.nome.charAt(0).toUpperCase());
        }
    }, []);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (popupRef.current && !popupRef.current.contains(event.target)) {
                setIsOpen(false);
                setShowDetails(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('usuarios');
        window.location.href = '/login';
    };

    const toggleDetails = () => {
        setShowDetails(!showDetails);
    };

    const renderPopup = () => (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-40">
            <div ref={popupRef} className="bg-gray-700 text-white p-8 rounded-lg shadow-lg w-64 text-center space-y-4">
                <div className="text-2xl font-semibold">{user?.nome}</div>

                {user?.role !== 'admin' && (
                    <>
                        <button
                            onClick={toggleDetails}
                            className="w-full py-2 text-gray-300 hover:text-white"
                        >
                            Dados do usuário
                        </button>

                        {showDetails && (
                            <div className="text-sm text-gray-300 space-y-2">
                                <div>CPF: {user?.cpf}</div>
                                <div>Telefone: {user?.telefone}</div>
                                <div>Email: {user?.email}</div>
                            </div>
                        )}
                        
                        <Link 
                            to="/pontuacao" 
                            className="block py-2 text-gray-300 hover:text-white"
                        >
                            Pontuação
                        </Link>
                    </>
                )}

                <button
                    onClick={handleLogout}
                    className="w-full py-2 bg-red-500 rounded-md hover:bg-red-600"
                >
                    Logout
                </button>
            </div>
        </div>
    );

    return (
        <header>
            <nav className={styles.navbar}>
                <div className="relative">
                    <button
                        className="flex items-center justify-center w-10 h-10 rounded-full bg-black text-white text-xl"
                        onClick={() => setIsOpen(!isOpen)}
                    >
                        {firstLetter}
                    </button>

                    {isOpen && createPortal(renderPopup(), document.body)}
                </div>
                {children}
                <span className={styles.brand}>JUSTIX</span>
            </nav>
        </header>
    );
}

export default HeaderLggd;
