import React, { useState } from 'react';
import styles from "./Header.module.css";

const Header = ({ sections, children }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredItems, setFilteredItems] = useState([]);

  const handleSearch = (e) => {
    e.preventDefault();
    if (sections && sections.length > 0) {
      const filtered = sections.flatMap(section => 
        section.items.filter(item => item.title.toLowerCase().includes(searchQuery.toLowerCase()))
      );
      setFilteredItems(filtered);
    }
  };

  return (
    <header className={styles.header}>
      <div className={styles.navbar}>
        <span className={styles.brand}>JUSTIX</span>
        
        
          <div className={styles.searchContainer}>
            <form onSubmit={handleSearch} className={styles.searchForm}>
              <input
                type="text"
                placeholder="Pesquisar..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className={styles.searchInput}
              />
              <button type="submit" className={styles.searchButton}>
                Buscar
              </button>
            </form>
            
            {filteredItems.length > 0 && (
              <div className={styles.megaMenu}>
                {filteredItems.map((item, index) => (
                  <div key={index} className={styles.menuItem}>
                    <a href={item.link}>
                      <p className={styles.itemTitle}>{item.title}</p>
                    </a>
                  </div>
                ))}
              </div>
            )}
          </div>
         : (
          <div className={styles.linkContainer}>{children}</div>
        )
      </div>
    </header>
  );
};

export default Header;
