import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import { Star, StarHalf } from 'lucide-react';
import styles from "../../Home/FAudiencias/Feed.module.css";
import HeaderLggd from "../../../components/HeaderLggd";
import Footer from  "../../../components/Footer/index";

const UFeedAudiencias = () => {
    const { id_juiz } = useParams();
    const [comments, setComments] = useState([]);
    const [averageRating, setAverageRating] = useState(0);
    const [juizData, setTribunalData] = useState(null);
    const [ratingStats, setRatingStats] = useState({
        5: 0,
        4: 0,
        3: 0,
        2: 0,
        1: 0
    });
    const [filteredRating, setFilteredRating] = useState(null);
    const [userRating, setUserRating] = useState(null);

    useEffect(() => {
        if (id_juiz) {
            fetchComments();
            fetchAverageRating();
            fetchTribunalData();
            fetchUserRating();
        }
    }, [id_juiz]);

    const fetchUserRating = async () => {
        try {
            // Assumindo que você tem acesso ao id do usuário logado
            const userId = localStorage.getItem('userId'); // ou onde você armazena o ID do usuário
            if (userId) {
                const res = await axios.get(`http://localhost:3001/juiz_avaliacao_usuario/${id_juiz}/${userId}`);
                if (res.data.avaliacoes) {
                    setUserRating(res.data.avaliacoes);
                }
            }
        } catch (error) {
            console.error('Erro ao buscar avaliação do usuário:', error);
        }
    };

    const fetchComments = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/av_juiz/${id_juiz}`);
            const commentsData = res.data;
            setComments(commentsData);
            calculateRatingStats(commentsData);
        } catch (error) {
            console.error('Erro ao buscar comentários:', error);
        }
    };

    const calculateRatingStats = (commentsData) => {
        const stats = { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };
        commentsData.forEach(comment => {
            if (comment.media_geral) {
                const rating = Math.floor(comment.media_geral);
                if (stats[rating] !== undefined) {
                    stats[rating]++;
                }
            }
        });
        setRatingStats(stats);
    };

    const fetchAverageRating = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/juiz_avaliacao/${id_juiz}`);
            const rating = res.data.media_ponderada ? parseFloat(res.data.media_ponderada) : 0;
            setAverageRating(rating);
        } catch (error) {
            console.error('Erro ao calcular média de avaliações:', error);
            setAverageRating(0);
        }
    };

    const fetchTribunalData = async () => {
        try {
            const res = await axios.get(`http://localhost:3001/juiz/${id_juiz}`);
            setTribunalData(res.data);
        } catch (error) {
            console.error('Erro ao buscar dados do juiz:', error);
        }
    };

    const getImagemUrl = (imagem) => {
        if (!imagem) return null;
        if (imagem.startsWith('/uploads/juiz/')) {
            return `http://localhost:3001${imagem}`;
        }
        return `http://localhost:3001/uploads/juiz/${imagem}`;
    };

    const RatingBar = ({ rating, count, totalReviews }) => {
        const percentage = totalReviews > 0 ? (count / totalReviews) * 100 : 0;
        return (
            <div
                className={styles.ratingbar}
                onClick={() => setFilteredRating(rating)}
                style={{ cursor: 'pointer' }}
            >
                <span className={styles.ratingnumber}>{rating}</span>
                <div className={styles.progressbar}>
                    <div
                        className={styles.progressfill}
                        style={{ width: `${percentage}%` }}
                    />
                </div>
                <span className={styles.ratingcount}>{count}</span>
            </div>
        );
    };

    const RatingStars = ({ rating }) => {
        const ratingNumber = parseFloat(rating) || 0;
        const fullStars = Math.floor(ratingNumber);
        const hasHalfStar = ratingNumber % 1 >= 0.5;

        return (
            <div className={styles.ratingstars}>
                {[...Array(fullStars)].map((_, i) => (
                    <Star key={i} className={styles.starfilled} />
                ))}
                {hasHalfStar && <StarHalf className={styles.starhalf} />}
                {[...Array(5 - fullStars - (hasHalfStar ? 1 : 0))].map((_, i) => (
                    <Star key={`empty-${i}`} className={styles.starempty} />
                ))}
            </div>
        );
    };

    const UserRatingDisplay = () => {
        if (!userRating) return null;

        const criterios = {
            produtividade: "Produtividade",
            fundamentacao: "Fundamentação",
            pontualidade: "Pontualidade",
            organizacao: "Organização",
            atendimento: "Atendimento"
        };

        return (
            <div className={styles.userRatingContainer}>
                <h3>Sua Avaliação</h3>
                <div className={styles.criteriosGrid}>
                    {Object.entries(criterios).map(([key, label]) => (
                        <div key={key} className={styles.criterioItem}>
                            <span className={styles.criterioLabel}>{label}</span>
                            <div className={styles.criterioValue}>
                                <RatingStars rating={userRating[key]} />
                                <span>{userRating[key]}</span>
                            </div>
                        </div>
                    ))}
                </div>
                <div className={styles.mediaGeral}>
                    <span>Média Geral:</span>
                    <div className={styles.mediaValue}>
                        <RatingStars rating={userRating.media_geral} />
                        <span>{userRating.media_geral}</span>
                    </div>
                </div>
            </div>
        );
    };

    const totalReviews = comments.length;
    const filteredComments = filteredRating
        ? comments.filter(comment => Math.floor(comment.media_geral || 0) === filteredRating)
        : comments;

    return (
        <>
            <HeaderLggd/>
            <div className={styles.feedcontainer}>
                <div className={styles.feedheader}>
                    {juizData && (
                        <h1 className={styles.maintitle}>{juizData.nome}</h1>
                    )}
                </div>

                {juizData && (
                    <div className={styles.juizinfo}>
                        <div className={styles.juizimagecontainer}>
                            {juizData.imagem ? (
                                <img
                                    src={getImagemUrl(juizData.imagem)}
                                    alt={juizData.nome}
                                    className={styles.juizimage}
                                />
                            ) : (
                                <div className={styles.noimageplacement}>
                                    Sem imagem disponível
                                </div>
                            )}
                        </div>
                        <div className={styles.juizdetails}>
                            <div className={styles.addressinfo}>
                                <h3 className={styles.addresstitle}>Informações de Localização</h3>
                                <p className={styles.addresstext}>
                                    {juizData.tempo_servico} anos de experiência
                                    <br />
                                    {juizData.casos_julgados} casos julgados   
                                </p>
                            </div>
                            <p className={styles.juizdescription}>{juizData.descricao}</p>
                        </div>
                    </div>
                )}

                <div className={styles.ratingscard}>
                    <div className={styles.ratingsoverview}>
                        <div className={styles.averageratingcontainer}>
                            <div className={styles.hugerating}>{averageRating.toFixed(1)}</div>
                            <RatingStars rating={averageRating} />
                            <div className={styles.totalreviews}>
                                {totalReviews.toLocaleString()} avaliações
                            </div>
                        </div>

                        <div className={styles.ratingdistribution}>
                            {[5, 4, 3, 2, 1].map(rating => (
                                <RatingBar
                                    key={rating}
                                    rating={rating}
                                    count={ratingStats[rating]}
                                    totalReviews={totalReviews}
                                />
                            ))}
                        </div>
                    </div>
                </div>

                {/* Exibição da avaliação do usuário */}
                <UserRatingDisplay />

                <div className={styles.feedbackbuttoncontainer}>
                    <Link to={`/user/juiz/${id_juiz}/feedback/add`} className={styles.feedbackbutton}>
                        {userRating ? 'Atualizar seu Feedback' : 'Realizar seu Feedback'}
                    </Link>
                </div>

                <div className={styles.commentssection}>
                    <h2 className={styles.commentstitle}>Comentários e Avaliações</h2>

                    {filteredRating && (
                        <div className={styles.clearfilter}>
                            <button onClick={() => setFilteredRating(null)}>
                                Limpar filtro
                            </button>
                        </div>
                    )}

                    <div className={styles.commentslist}>
                        {filteredComments.length > 0 ? (
                            filteredComments.map((comment, index) => (
                                <div key={index} className={styles.commentcard}>
                                    <div className={styles.commentheader}>
                                        <div className={styles.commentuser}>
                                            <div className={styles.useravatar}>
                                                {comment.nome ? comment.nome[0].toUpperCase() : `${comment.id_usuario}`}
                                            </div>
                                            <span className={styles.username}>
                                                {comment.nome || `anonimous${comment.id_usuario}`}
                                            </span>
                                        </div>
                                        <RatingStars rating={comment.media_geral}/>
                                    </div>
                                    <p className={styles.commenttext}>{comment.comentario}</p>
                                    <div className={styles.commentdate}>
                                        {new Date(comment.data_criacao).toLocaleDateString('pt-BR', {
                                            year: 'numeric',
                                            month: 'long',
                                            day: 'numeric'
                                        })}
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p className={styles.nocomments}>Nenhum comentário disponível.</p>
                        )}
                    </div>
                </div>
            </div>
            <Footer/>
        </>
    );
};

export default UFeedAudiencias;
