import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import styles from './HeaderLggd.module.css';

function HeaderLggd({ children }) {
    const [isOpen, setIsOpen] = useState(false);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [showDetails, setShowDetails] = useState(false);
    const [showProgress, setShowProgress] = useState(false);
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
    const [user, setUser] = useState(null);
    const [firstLetter, setFirstLetter] = useState('');
    const [userColor, setUserColor] = useState('#000000');
    const [progress, setProgress] = useState(0);
    const [rewardToken, setRewardToken] = useState('');
    const [overlayVisible, setOverlayVisible] = useState(false); // Controlar o overlay
    const [searchQuery, setSearchQuery] = useState('');
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [searchResults, setSearchResults] = useState([]);

    const sidebarRef = useRef(null);
    const searchRef = useRef(null);

    useEffect(() => {
        // Ajustar para dispositivos móveis
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 768);
        };
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    useEffect(() => {
        // Buscar usuário no localStorage
        const storedUser = JSON.parse(localStorage.getItem('usuarios'));
        if (storedUser) {
            setUser(storedUser);
            setFirstLetter(storedUser.nome.charAt(0).toUpperCase());
            let storedUserColor = localStorage.getItem('userColor');
            if (!storedUserColor) {
                storedUserColor = generateRandomColor();
                localStorage.setItem('userColor', storedUserColor);
            }
            setUserColor(storedUserColor);
            const storedProgress = localStorage.getItem(`userProgress_${storedUser.id}`);
            setProgress(storedProgress ? parseInt(storedProgress) : 0);
        }
    }, []);

    useEffect(() => {
        // Fechar a sidebar ao clicar fora
        const handleClickOutside = (event) => {
            if (sidebarRef.current && !sidebarRef.current.contains(event.target)) {
                if (isMobile) {
                    setIsOpen(false);
                    setOverlayVisible(false);
                }
            }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, [isMobile]);

    const handleGenerateToken = () => {
        const token = generateRandomToken();
        setRewardToken(token);
        localStorage.setItem('rewardToken', token);
        setProgress(0); // Resetar o progresso ao gerar o token
        localStorage.setItem(`userProgress_${user.id}`, 0); // Resetar o progresso no localStorage
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('usuarios');
        localStorage.removeItem('userColor');
        window.location.href = '/';
    };

    const generateRandomColor = () => {
        const colors = [
            '#2196F3', '#4CAF50', '#9C27B0', '#F44336', '#FF9800',
            '#009688', '#3F51B5', '#795548', '#607D8B', '#E91E63'
        ];
        return colors[Math.floor(Math.random() * colors.length)];
    };

    const generateRandomToken = () => {
        return Math.random().toString(36).substring(2, 15); // Exemplo simples de token
    };

    const handleInputChange = (e) => {
        const query = e.target.value;
        setSearchQuery(query);
        setIsMenuOpen(!!query);
        if (query) {
            fetchSearchResults(query);
        } else {
            setSearchResults([]);
        }
    };

    const fetchSearchResults = async (query) => {
        // Implementação fictícia de busca
        // Substitua pelo seu código de busca real
        setSearchResults([
            { name: 'Resultado 1', link: '/link1' },
            { name: 'Resultado 2', link: '/link2' }
        ]);
    };

    const UserPanel = () => (
        <div className={styles.sidebarContent}>
            <div className={styles.userHeader}>
                <div className={styles.usuario} style={{ backgroundColor: userColor }}>
                    {firstLetter}
                </div>
                <div className={styles.userName}>{user?.nome}</div>
            </div>

            {user?.role !== 'admin' && (
                <>
                    <button
                        onClick={() => setShowDetails(!showDetails)}
                        className={styles.menuItem}
                    >
                        Dados do usuário
                    </button>

                    {showDetails && (
                        <div className={styles.userDetails}>
                            <div>CPF: {user?.cpf || 'Não disponível'}</div>
                            <div>Telefone: {user?.telefone || 'Não disponível'}</div>
                            <div>Email: {user?.email || 'Não disponível'}</div>
                        </div>
                    )}

                    <button
                        onClick={() => setShowProgress(!showProgress)}
                        className={styles.menuItem}
                    >
                        Pontuação
                    </button>

                    {showProgress && (
                        <div className={styles.progressContainer}>
                            <div className={styles.progressBar}>
                                <div 
                                    className={styles.progressFill}
                                    style={{ width: `${(progress / 1000) * 100}%` }}
                                />
                            </div>
                            <div className={styles.progressText}>
                                {progress} / 1000 pontos
                            </div>
                            {progress >= 1000 && !rewardToken && (
                                <button
                                    onClick={handleGenerateToken}
                                    className={styles.rewardButton}
                                >
                                    Gerar Token de Recompensa
                                </button>
                            )}
                            {rewardToken && (
                                <div className={styles.tokenContainer}>
                                    <p>Seu Token:</p>
                                    <p>{rewardToken}</p>
                                </div>
                            )}
                        </div>
                    )}
                </>
            )}

            <button
                onClick={handleLogout}
                className={styles.menuItem}
            >
                Logout
            </button>
        </div>
    );

    return (
        <header>
            <nav className={styles.navbar}>
                <Link to="/user/" className={styles.brandL}>
                    <span className={styles.brand}>JUSTIX</span>
                </Link>

                <div className={styles.searchContainer} ref={searchRef}>
                    <form onSubmit={(e) => e.preventDefault()} className={styles.searchForm}>
                        <div className={styles.searchWrapper}>
                            <input
                                type="text"
                                placeholder="Pesquisar..."
                                value={searchQuery}
                                onChange={handleInputChange}
                                className={styles.searchInput}
                            />
                            <button type="submit" className={styles.searchButton}>
                                Buscar
                            </button>
                        </div>
                    </form>
                    {isMenuOpen && searchResults.length > 0 && (
                        <div className={styles.megaMenu}>
                            <ul>
                                {searchResults.map((result, index) => (
                                    <li key={index}>
                                        <Link to={result.link} onClick={() => setIsMenuOpen(false)}>
                                            {result.name}
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>

                <div className={styles.navleft}>
                    <div 
                        className={styles.dropdown}
                        onMouseEnter={() => setIsDropdownOpen(true)}
                        onMouseLeave={() => setIsDropdownOpen(false)}
                    >
                        <button className={styles.navlinks}>Opções de busca</button>
                        {isDropdownOpen && (
                            <div className={styles.dropdownContent}>
                                <Link to="/user/tribunais">Tribunais</Link>
                                <Link to="/user/foruns">Fóruns</Link>
                                <Link to="/user/juiz">Audiencias</Link>
                                <Link to="/user/mediacoes">Mediações</Link>
                                <Link to="/user/advocacia">Advocacia</Link>
                                <Link to="/user/portais">Portais</Link>
                            </div>
                        )}
                    </div>

                    <button
                        className={styles.avatarButton}
                        onClick={() => {
                            setIsOpen(!isOpen);
                            setOverlayVisible(!overlayVisible);
                        }}
                    >
                        {firstLetter}
                    </button>
                </div>
            </nav>

            {isOpen && (
                <>
                    {overlayVisible && <div className={styles.overlay}></div>}
                    <div className={styles.sidebar} ref={sidebarRef}>
                        <UserPanel />
                    </div>
                </>
            )}
        </header>
    );
}

export default HeaderLggd;
